/* ==UserStyle==
@name         YouTube: Fade-In Video Thumbnails
@namespace    de.sidneys.userstyles
@version      1.0.0
@description  This user style lets video thumbnails on YouTube blend in smoothly, as soon as they have been downloaded. Whilst reducing perceived UI jankiness this helps avoiding eye strain – in context of long, infinitely scrolling list elements.
@author       sidneys
@homepageURL  https://userstyles.org/users/301302
@supportURL   https://userstyles.org/users/301302
@license      MIT
@preprocessor default
==/UserStyle== */
@-moz-document domain("youtube.com")
{
    /* Thumbnail background
       ==================================== */

    ytd-thumbnail #thumbnail.ytd-thumbnail
    {
        background-color: hsla(0deg, 0%, 100%, 3%);
    }

    /* Thumbnail image
       ==================================== */

    .ytd-thumbnail > img.yt-img-shadow
    {
        transition: filter 600ms ease-in-out 200ms;
        filter: opacity(0%);
    }

    /* Thumbnail image, loaded
       ==================================== */

    .ytd-thumbnail > img.yt-img-shadow[src^="http"]
    {
        filter: opacity(100%);
    }
}
