css:
@-moz-document domain(forums.iracing.com){
    /****** Home *******/
    /* Featured Discussions */
    .Section-CategoryList [class*="homeWidgetContainer"] {
        display: none;
    }

    /* Remove Top Page Description */
    .Section-CategoryList .MainContent .pageHeadingBox .PageDescription {
        display: none;
    }

    .Section-CategoryList .MainContent .pageHeadingBox .HomepageTitle {
        font-size: 24px !important;
    }

    .Section-CategoryList .Content .PageControls.Top .PageControls-filters {
        justify-content: flex-end;
        margin-top: -2rem;
    }

    .Section-CategoryList .MainContent .DataList div[role="heading"] {
        font-size: 18px !important;
    }

    .Section-CategoryList .MainContent .DataList .Item.pageBox:nth-child(2) {
        border-top-right-radius: 7px;
        border-top-left-radius: 7px;
    }

    .Section-CategoryList .MainContent .DataList .Item.pageBox:last-child {
        border-bottom-right-radius: 7px;
        border-bottom-left-radius: 7px;
    }

    .Section-CategoryList .MainContent .DataList .Item.pageBox {
        margin-top: 0 !important;
        padding: 0.5rem 1.25rem;
        border-radius: 0;
        border-bottom: 1px solid #666;
    }

    .Section-CategoryList .MainContent .DataList .Item.pageBox:last-child {
        border-bottom: none;
    }

    .Section-CategoryList .MainContent .DataList .Item.pageBox .Item-Icon {
        width: 22px;
        height: 22px;
    }

    .Section-CategoryList .MainContent .DataList .Item.pageBox .Title {
        margin-bottom: 0;
    }

    .Section-CategoryList .MainContent .DataList .Item.pageBox .Meta {
        display: inline-flex;
    }

    .Section-CategoryList .MainContent .DataList .Item.pageBox .Meta .MItem.CommentCount {
        margin-right: 15px;
    }

    .Section-CategoryList .MainContent .DataList .Item.pageBox .Meta .MItem.CommentCount:after {
        content: '|';
        margin-left: 15px;
    }

    /* Thread List */
    .Section-DiscussionList .PageDescription {
        display: none;
    }
    .Section-DiscussionList .PageControls {
        float: right;
        padding-right: 1.25rem;
    }
    .Section-DiscussionList .pageHeadingBox + .PageControls {
        margin-top: -2.5rem;
    }

    .Section-DiscussionList [class*="PageBox-boxClass"] {
        margin-top: 0 !important;
        border-radius: 0;
        border-bottom: 1px solid #666;
        padding: 0.75rem 1rem;
        position: relative;
    }
    .Section-DiscussionList [class*="PageBox-boxClass"]:last-child {
        border-bottom: none;
    }

    .Section-DiscussionList [class*="ListItem-styles-description"] {
        display: none;
    }

    .Section-DiscussionList [class*="ListItem-styles-metaDescriptionContainer"] {
        margin-top: 5px;
    }

    .Section-DiscussionList [class*="ListItem-styles-metasContainer"] {
        display: flex;
        margin-top: 0;
        align-items: center;
    }

    .Section-DiscussionList [class*="metas-meta"] > div[class*="Notice-styles-root"] {
        clip: rect(0, 0, 0, 0);
        padding: 0;
        font-size: 0;
        border: 0;
        overflow: hidden;
    }

    .Section-DiscussionList [class*="metas-meta"] > div[class*="Notice-styles-root"]:before {
        content: '';
        background-color: #006eff;
        height: 100%;
        width: 5px;
        display: block;
        position: absolute;
        top: 50%;
        left: 0px;
        border-radius: 0;
        transform: translateY(-50%);
    }

    .Section-DiscussionList [class*="metas-metaLabel"] {
        background-color: red;
        border-color: red;
        font-size: 11px;
        padding: 0px 7px;
    }

    .Section-DiscussionList [class*="metas-metaIcon"] {
        display: inline-flex;
        align-items: center;
    }

    .Section-DiscussionList [class*="metas-metaIcon"] svg {
        vertical-align: middle;
        transform: none;
    }

    .Section-DiscussionList div[class*="metas-metaIcon"]::first-of-type {
        margin-left: auto;
    }

    /* Titles */
    .Discussion .PageTitle h1 {
        font-size: 28px !important;
    }

    .pageHeadingBox .CommentHeading {
        font-size: 18px !important;
    }

    /* Message List */
    .Section-Discussion .MessageList.Comments,
    .Section-Discussion .MessageList .Item {
        padding: 0 !important;
    }

    .Section-Discussion .MessageList.DataList {
        background: none !important;
    }

    .Section-Discussion .MessageList .Item {
        margin-top: 20px !important;
    }

    .Section-Discussion .MessageList .Item:before,
    .Section-Discussion .MessageList .Item:after {
        content: none !important;
    }

    .Section-Discussion .MessageList .Item.Rank-Admin .Item-Header.DiscussionHeader,
    .Section-Discussion .MessageList .Item.Rank-Admin .Item-Header.CommentHeader {
        background-color: rgb(223, 39, 39, 0.5);
    }

    .Section-Discussion .MessageList .Item .Item-Header.DiscussionHeader,
    .Section-Discussion .MessageList .Item .Item-Header.CommentHeader {
        padding: 0.75rem 1.25rem 0.5rem;
        background-color: rgba(255, 255, 255, 0.09);
    }

    .Section-Discussion .DataList .Options,
    .Section-Discussion .MessageList .Options {
        align-self: flex-end;
        position: relative;
        top: 5px;
    }

    .Section-Discussion .MessageList .Item .Message {
        padding: 1.25rem;
        margin-top: 0;
    }

    .Section-Discussion .MessageList .Item .Message a:not(:hover) {
        color: #62a6ff;
    }

    .Section-Discussion .MessageList .Item .Message [class*="-quoteEmbed-header"],
    .Section-Discussion .MessageList .Item .Message [class*="-quoteEmbed-content"],
    .Section-Discussion .MessageList .Item .Message [class*="-collapsableContent-footer"],
    .Section-Discussion .MessageList .Item .Message [class*="embedExternal-content"] {
        background-color: rgba(255, 255, 255, 0.15);
    }

    .Section-Discussion .MessageList .Item .Item-Header .Author .Username {
        font-size: 15px;
    }

    .Section-Discussion .MessageList .Item .Item-Header .CommentMeta > .MItem,
    .Section-Discussion .MessageList .Item .Item-Header .CommentMeta > .MItem a {
        font-size: 11px;
        color: #bbb;
    }

    .Section-Discussion .MessageList .Item .Item-Header .Rank.MItem:not(.Rank-Admin) {
        font-size: 0;
    }

    .Section-Discussion .MessageList .Item .Item-Header .Rank.MItem:not(.Rank-Admin):before {
        color: white;
        padding: 3px 5px;
        border-radius: 3px;
        text-transform: uppercase;
        font-weight: bold;
        content: attr(title);
    }

    .Section-Discussion .MessageList .Item .Item-Header .Rank.MItem::before {
        font-size: 11px;
    }

    .Section-Discussion .MessageList .Item .Item-Header .Rank.MItem[title="Forum Rookie"]:before {
        background-color: #e2211d;
    }

    .Section-Discussion .MessageList .Item .Item-Header .Rank.MItem[title="Forum D License"]:before {
        background-color: #ff9300;
    }

    .Section-Discussion .MessageList .Item .Item-Header .Rank.MItem[title="Forum C License"]:before {
        background-color: #ded311;
    }

    .Section-Discussion .MessageList .Item .Item-Header .Rank.MItem[title="Forum B License"]:before {
        background-color: #00bf25;
    }

    .Section-Discussion .MessageList .Item .Item-Header .Rank.MItem[title="Forum A License"]:before {
        background-color: #006eff;
    }

    .Section-Discussion .MessageList .Item .Item-Header .Rank.MItem[title="Forum Pro"]:before {
        background-color: #000000;
    }

    .Section-Discussion .MessageList .Item .Item-Header .Rank.MItem.Rank-Admin {
        background-color: white !important;
        color: #df2826 !important;
    }

    .Section-Discussion .DataList .PhotoWrap,
    .Section-Discussion .MessageList .PhotoWrap {
        width: 38px;
        height: 38px;
    }
    .Section-Discussion .MessageList .Item .Signature {
        box-shadow: 0 0 20px 0 rgba(0, 0, 0, 0.3) inset;
        padding: 1.25rem;
        background-color: rgba(0, 0, 0, 0.4);
        font-size: 12px;
        color: #ccc;
    }

    /* clear floated elements insie signatures */
    .Section-Discussion .MessageList .Item .Signature {
        overflow: hidden;
    }

    .Section-Discussion .MessageList .ItemDiscussion .InlineTags {
        padding: 0.5rem 1.25rem;
    }

    .Section-Discussion .MessageList .Item .Message .code {
        background-color: #777;
    }


    /***** Navigation *****/
    .Pager.NumberedPager .Pager-p {
        color: white;
        border-color: #666;
        transition: all 0.2s ease-in-out;
    }
    .Pager.NumberedPager .Pager-p:hover,
    .Pager.NumberedPager .Previous:hover,
    .Pager.NumberedPager .Next:hover {
        background-color: rgba(255, 255, 255, .25);
    }

    .Pager.NumberedPager .Pager-p.Highlight {
        background-color: #006eff !important;
    }

    .Pager.NumberedPager .Previous,
    .Pager.NumberedPager .Next {
        color: white !important;
        border-color: #666;
    }

    /* Reactions */
    .Reactions {
        position: absolute;
        top: 5px;
        right: 0px;
        justify-content: flex-end;
        margin: 0 !important;
        width: auto;
        padding: 0.25rem 0.75rem !important;
        background: none;
    }
    .Reactions .ReactLabel {
        display: none;
    }
    .Reactions .ReactButton.Quote {
        font-size: 0;
    }

    /****** Messages ******/

    .Section-Conversation .MessageList { 
        padding: 0 !important;
        background: none !important;
    }

    .Section-Conversation .MessageList .Item {
        padding: 1rem 1.25rem !important;
    }

    .Section-Conversation .MessageList .Item:after,
    .Section-Conversation .MessageList .Item:before {
       content: none !important;
    }

    /****** Badges Page ******/
    .Badges .Panel.Panel-main > .pageBox {
        display: flex;
        flex-direction: column;
    }

    .Badges .Panel.Panel-main > .pageBox > a {
        order: 100;
        margin-top: 2rem !important;
    }

    .Badges .DataList.Badges {
        display: flex;
        flex-direction: column;
    }

    .Badges .DataList.Badges .Item {
        background-color: rgba(255, 255, 255, 0.1);
        padding-left: 1rem;
        border-bottom: 1px solid rgba(255, 255, 255, 0.25);
    }

    .Badges .DataList.Badges .Item.Read {
        background-color: rgba(0, 0, 0, 0.4);
        order: 10;
    }

    .Badges .DataList.Badges .Item:before {
        content: '';
        background-color: #006eff;
        width: 4px;
        height: 100%;
        display: block;
        position: absolute;
        left: 0;
        top: 0;
    }
    .Badges .DataList.Badges .Item .Meta .MItem,
    .Badges .DataList.Badges .Item .Meta p {
        color: #bbb;
    }
    .Badges .DataList.Badges .Item {
        position: relative;
        padding-bottom: 1.5rem;
    }
    .Badges .DataList.Badges .Item .Meta p {
        margin-left: 5px;
        font-size: 11px;
        padding: 4px 4px 4px 66px !important;
        position: absolute;
        bottom: 0;
        left: 0px;
        background-color: rgba(255, 255, 255, 0.05);
        width: calc(100% - 5px);
    }

    .Badges .DataList.Badges .Item.Read:before {
        content: none;
    }

    .Badges .Box.Leaderboard .pageHeadingBox {
        background-color: rgba(255, 255, 255, 0.1);
        padding: 0.65rem 0.75rem 0.3rem !important;
    }

    .Badges .Box.Leaderboard .Leaderboard__title {
        font-size: 18px !important;
    }

    .Badges .Box.Leaderboard .Leaderboard__user-list .Leaderboard__user-list__item {
        padding: 0.35rem 0.75rem !important;
        border-bottom: 1px solid #444;
        transition: all 0.2s ease-in-out;
    }

    .Badges .Box.Leaderboard .Leaderboard__user-list .Leaderboard__user-list__item:hover {
        background-color: rgba(0, 0, 0, 0.25);
    }

    .Badges .Box.Leaderboard .Leaderboard__user-list .Leaderboard__user-list__item .Username {
        color: white;
    }

    .Badges .Box.Leaderboard .Leaderboard__user-list .Leaderboard__user-list__item .Count {
        color: #ddd;
    }
}
