css:
@-moz-document domain("www.roblox.com"), domain("web.roblox.com") {
.light-theme .rbx-header {
    background-color: #2D528F;
    color: #fff;
    font-family: 'Source Sans Pro';
    height: 40px!important;
}
.rbx-header .rbx-navbar-header {
    max-width: 1106px;
    margin-top: -3px;
}
.light-theme .icon-logo-r {
    background: url('http://web.archive.org/web/20140909004642im_/https://www.roblox.com/images/Logo/roblox_icon.png') no-repeat 4px center;
    width: 35px;
    height: 34px;
    margin-left: 12px;
}
.light-theme .icon-logo {
    background-image: url(http://web.archive.org/web/20140916012341im_/http://www.roblox.com/images/Icons/Navigation2014/roblox_logo.png);
    margin-top: 3px;
    width: 120px;
    height: 30px;
    margin-left: 10px;
}
.light-theme .rbx-header .icon-robux-28x28 {
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    background-position:-3px -393px!important;
    width:35px;
    height:30px;
    margin-top: -8px !important;
}
.rbx-header .rbx-navbar-icon-group li span[class^="rbx-text"] {
    min-width: 12px;
    margin-right: 12px;
    margin-top: -9px !important;
}
.light-theme .rbx-header .input-group .input-group-btn .input-addon-btn {
    right: 0!important;
    left: unset;
}
.light-theme .rbx-header .icon-common-search-sm {
    background-position: -12px -335px !important;
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    width: 37px;
}
.rbx-header .navbar-search .input-addon-btn .icon-common-search-sm {
    opacity: 1;
}
.light-theme .rbx-header .navbar-search .input-field {
    padding-left: 10px;
    font-family: 'Source Sans Pro';
    border: none;
}
.light-theme .icon-nav-settings {
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    background-position:-4px -456px;
    height:29px;
    width: 35px;
    margin-top: -8px !important;
}

.rbx-header .rbx-navbar-icon-group .navbar-stream {
    display: none;
}
.light-theme .icon-logo-r:hover, .light-theme .icon-nav-menu:hover {
    background-color: #27487E;
    border-radius: 4px;
}
.light-theme .rbx-header .text-header {
    color: white!important;
}
.light-theme .icon-nav-menu {
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    background-position: 2px -317px;
    height: 33px;
    width: 46px;
}
.rbx-header .age-bracket-label, .rbx-left-col li .text-nav .avatar-headshot-xs {
    display: none!important;
}
.light-theme .rbx-left-col {
    box-shadow: none;
    font-family: 'Source Sans Pro';
}
.light-theme .rbx-left-col .rbx-scrollbar .left-col-list {
    padding: 0px;
}
.rbx-left-col #nav-home, .rbx-left-col #nav-message, .rbx-left-col #nav-profile, .rbx-left-col #nav-friends, .rbx-left-col #nav-character, .rbx-left-col #nav-trade, .rbx-left-col #nav-inventory, .rbx-left-col #nav-group, .rbx-left-col #nav-my-feed, .rbx-left-col #nav-blog, .rbx-left-col #nav-shop, .rbx-left-col #nav-giftcards {
    padding: 6px 7px;
}
.rbx-left-col .left-col-list li {
    margin: 0px;
}
.rbx-left-col li .text-nav .font-header-2{
    margin-left: 7px!important;
}
#navigation ul {
    padding: 0px;
}
.rbx-left-col #nav-home:hover, .rbx-left-col #nav-message:hover, .rbx-left-col #nav-profile:hover, .rbx-left-col #nav-friends:hover, .rbx-left-col #nav-character:hover, .rbx-left-col #nav-trade:hover, .rbx-left-col #nav-inventory:hover, .rbx-left-col #nav-group:hover, .rbx-left-col #nav-my-feed:hover, .rbx-left-col #nav-blog:hover, .rbx-left-col #nav-shop:hover, .rbx-left-col #nav-giftcards:hover {
    color: #000;
    background-color: #CACACA;
}
.rbx-left-col.nav-show .rbx-scrollbar li {
    width: 175px;
}
.light-theme .rbx-left-col .icon-nav-home {
    background-position: -5px -3px;
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
}
.light-theme .rbx-left-col .icon-nav-profile {
    background-position: -5px -32px;
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
}
.light-theme .rbx-left-col .icon-nav-message {
        background-position: -5px -59px;
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
}
.light-theme .rbx-left-col .icon-nav-friends {
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    background-position:-5px -90px;
}
.light-theme .rbx-left-col .icon-nav-charactercustomizer {
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    background-position:-5px -119px;
}
.light-theme .rbx-left-col .icon-nav-inventory {
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    background-position:-5px -147px;
}
.light-theme .rbx-left-col .icon-nav-trade {
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    background-position:-5px -178px;
}
.light-theme .rbx-left-col .icon-nav-group {
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    background-position: -5px -235px;
    width: 33px;
}
.light-theme .rbx-left-col .icon-nav-my-feed {
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    background-position: -5px -293px;
}
.light-theme .rbx-left-col .icon-nav-blog {
    background-image: url(http://web.archive.org/web/20140909004641im_/http://www.roblox.com/images/Icons/Navigation2014/Nav2014-icon-sprite-sheet.png);
    background-position: -5px -263px;
}
.light-theme .rbx-left-col #nav-shop, .light-theme .rbx-left-col #nav-giftcards {
    display: none;
}
.light-theme .rbx-upgrade-now .btn-secondary-md {
    margin-left: 20px;
    margin-right: 20px;
    height: 31px;
    background-color: #2D528F;
    border: none;
    padding: 6px;
    border-radius: 4px;
}
.light-theme .rbx-upgrade-now .btn-secondary-md:hover {
    background-color: #27487E
}
.light-theme .rbx-left-col #nav-friends:hover {
    color: #000;
background-color: #CACACA;
}
.light-theme .rbx-left-col.nav-show .font-bold {
    font-size: 14px;
    color: #999;
    font-weight: bold;
    margin-left: 7px;
}
.light-theme .rbx-left-col .rbx-nav-sponsor {
    margin-left: 7px;
}
.light-theme .rbx-left-col .notification-blue {
    display: none!important;
}
.rbx-header .rbx-navbar li {
    width: 0;
    height: 0;
}
.light-theme .rbx-header .rbx-navbar li:hover {
    border: none;
}
.light-theme .rbx-header .rbx-navbar li .nav-menu-title:hover, .light-theme .rbx-header .rbx-navbar li .nav-menu-title:hover::before {
    background-color: #27487E;
    padding: 5px 10px;
    margin-top: 1px;
}
.rbx-header .rbx-navbar {
    margin-top: 3px;
}
}

@-moz-document domain("www.roblox.com"), domain("web.roblox.com") {
/* blah blah blah change text on da header and left navbar */

a.font-header-2.nav-menu-title.text-header[href="/games"] {
    visibility: hidden;
    font-size: 0px;
    height: 34px;
}

a.font-header-2.nav-menu-title.text-header[href="/games"]::before {
    font-size: 16px;
    content: "Games";
    height: 37px;
    visibility: visible;
    border-radius: 5px;
}

a.font-header-2.nav-menu-title.text-header[href="/catalog"] {
    visibility: hidden;
    font-size: 0px;
    height: 34px;
}

a.font-header-2.nav-menu-title.text-header[href="/catalog"]::before {
    font-size: 16px;
    content: "Catalog";
    height: 37px;
    visibility: visible;
    border-radius: 5px;
}

a.font-header-2.nav-menu-title.text-header[href="/develop"] {
    visibility: hidden;
    font-size: 0px;
    height: 34px;
}

a.font-header-2.nav-menu-title.text-header[href="/develop"]::before {
    font-size: 16px;
    content: "Develop";
    height: 37px;
    visibility: visible;
    border-radius: 5px;
}

a.font-header-2.nav-menu-title.text-header[href="/robux?ctx-nav"] {
    visibility: hidden;
    font-size: 0px;
    height: 34px;
}
a.font-header-2.nav-menu-title.text-header[href="/robux?ctx-nav"]::before {
    font-size: 16px;
    content: "ROBUX";
    height: 37px;
    visibility: visible;
    border-radius: 5px;
}

.rbx-left-col #nav-character .font-header-2 {
    visibility: hidden;
    font-size: 0px;
    height: 22px;
}
.rbx-left-col #nav-character .font-header-2::before {
    font-size: 16px;
    visibility: visible;
    content: "Character";
}
}

