css:
@-moz-document domain('v3rmillion.net') {

.current {
    transform: scale(1)!important;
    pointer-events: auto!important;
    opacity: 1!important;
}
.modal {
    box-shadow: 0 11px 15px -7px rgb(0 0 0 / 20%), 0 24px 38px 3px rgb(0 0 0 / 14%), 0 9px 46px 8px rgb(0 0 0 / 12%)!important;
    transition-duration: 250ms;
    transition-property: transform;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

#myalerts_alerts_modal{
    display: block!important;
    transform: scale(0.5);
    pointer-events: none;
    opacity: 0;
}

.modal a.close-modal {
    background: url(https://hotemoji.com/images/emoji/g/14kioe01bpckzg.png);
    top: 12px;
    right: 14px;
    width: 20px;
    height: 20px;
    background-size: cover;
}

tr th strong {
    font-size: 19px;
}


tr th strong:after {
    content: ' ✨';
}

#bridge {
    background: #151515;
    height: 45px;
    border-bottom: 5px solid #ff0000;
    padding-top: 8px;
    padding-bottom: 8px;
}


.forum_on {
    color: #ff0000;
}

.alert__time{
    text-align:center;
}

.alert--unread>td {
    background-color: #252525;
}

.thead{
    background:#101010;
}

#content .tborder thead tr .thead {
    background: url(https://cdn.discordapp.com/attachments/749570988559302686/852941966118223882/16233790964281545_2.png);
    background-size: cover;
    background-color: #000000;
    background-position: center;
}

#content{
    border:none;
    border-radius: 6px;
}

.navigation{
    border:none;
    border-radius:6px;
}

#header{
    background: url(https://cdn.discordapp.com/attachments/850874564854677504/852738796394512414/16233790964281545_1.png);
    background-size: cover;
    background-color: #250000;
}

#sharingPlace {
    display:none;
}

a:hover + .ddm, .ddm:hover, a:hover + .panel_buttons, .panel_buttons:hover {
    transform: translate(0%, 0%) scale(1);
    opacity: 1!important;
    pointer-events:auto;
}
ul.ddm, .panel_buttons{
    transition-timing-function: ease;
    transition-property: transform, opacity;
    transition-duration: 150ms;
    display: block!important;
    opacity:0!important;
    transform: translate(-21%, -20%) scale(0.5);
    pointer-events:none;
    border-top: 5px solid #c31715;
    border-radius: 8px;
    font-weight: 500;
    width: 188px!important;
}

.ddm_anchor{
    background:none!important;
}

ul.menu li:hover a, ul.menu li a:hover, .ddm_anchor a:hover {
    background: #1d1d1d;
    color: white;
}

.panel_buttons a {
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
}

ul.menu li a, .ddm_anchor a {
    margin-left: 5px;
    margin-right: 5px;
    transition: 100ms background ease;
    border-radius: 6px;
}
.ddm li a {
    font-size: 14px!important;
}

#container .navigation img{
    display:none;
}

#container .navigation .active:before{
    content: "› "
}

.clear tbody tr .thead{
    background: url(https://cdn.discordapp.com/attachments/749570988559302686/852941966118223882/16233790964281545_2.png);
    background-size: cover;
    background-color: #000000;
    background-position: center;
}

#content div .smalltext{
    overflow: hidden;
    white-space: nowrap;
}

.pages{
    display: none;
}

a.button.new_thread_button span:before {
    content: "\f040";
}

.new_thread_button, .new_reply_button{
    line-height: 24px;
    background: #f72828!important;
    border: none!important;
    transition:0.1s;
}

.new_thread_button:hover, .new_reply_button:hover{
    background: #d81e1e!important;
}

.pagination{
    font-size: 13px;
}

.pagination a, .pagination_current {
    background: #f72828;
    border: none!important;
    border-radius: 6px!important;
    padding-left: 8px!important;
    padding-right: 8px!important;
    padding-top: 4px!important;
    padding-bottom: 4px!important;
    font-weight: 500;
    transition:0.1s;
}

.pagination a:hover {
    background: #d81e1e!important;
}

#go_page_1{
    display:none;
}

.profile_header{
    border:none;
    border-radius:8px;
    padding-left:20px;
    padding-bottom:20px;
    padding-right:20px;
}

.profile_header table tbody tr td img{
    border-radius:8px;
}

.tfoot.smalltext{
    background: #111111;
    font-weight:600;
}

.post_head{
    border:none!important;
}

.postlink{
    display:none;
}

.post.classic .post_author{
    border: none!important;
    background: #131212!important;
    margin-left: 10px!important;
    margin-bottom: 15px!important;
    box-shadow: rgb(0 0 0 / 24%) 0px 3px 8px;
    padding: 10px;
}

.post .post_author div.author_avatar img{
    border: none;
    padding: 0;
    border-radius: 8px;
    box-shadow: rgb(0 0 0 / 24%) 0px 3px 8px;
}

.post.classic .post_author div.author_statistics {
    border: none;
    background: #1b1b1b;
    border-radius: 8px;
    padding: 10px;
}

.post .post_author{
    background: #1b1b1b;
    border:none;
}

span.post_date:before {
    content: "›";
    font-weight: 600;
    padding-right: 6px;
}

.post .post_head span.post_date {
    margin-left: -8px;
}

a.button.new_reply_button span:before {
    content: "\f040";
}

.sub_control{
color: white;
    border: none;
    background: #151515;
    padding: 7px;
    border-radius: 8px;
    padding-left: 10px;
    padding-right: 10px;
    font-weight: 500;
    transition:0.1s;
}

a{
    transition:0.1s;
}

.thread_head{
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    padding-left: 15px;
}

.post{
    padding-left: 10px;
    padding-right: 10px;
}

.author_statistics{
    border-radius:8px;
}

.classic{
    padding-left: 0;
    padding-right: 0;
}
}

@-moz-document url(http://v3rmillion.net/newthread.php) {
	input[name="submit"]{
    margin-bottom:15px;
}
}

@-moz-document url(http://v3rmillion.net/forumdisplay.php) {
	#content .float_left{
    margin-bottom: 20px!important;
    margin-top: 5px!important;
}

#content .float_right{
    margin-top: 8px;
}
}
