css:
@-moz-document url-prefix("https://www.youtube.com") {

/* Yeaaaaaah :3 */

 :root {
    --blur-amount: /*[[blur]]*/px;
    --theme: /*[[theme]]*/;
    --playlist-bg: rgba(/*[[rgba1]]*/);
    --text-color: /*[[text]]*/;
    --nd-text-color: /*[[sec]]*/;
    --border-width: /*[[border_n]]*/px;
    --player-bg-border-width: /*[[border_p]]*/px;
    --border-color: /*[[border_c]]*/;
    --border-hover-color: /*[[hover]]*/;
    --border-click-color: /*[[click]]*/;
    --bg-color: /*[[bg]]*/;
    --in-player-bg-color: rgba(/*[[p_bg]]*/);
    --top-bar-and-search-background: rgba(/*[[tb_bg]]*/);
    --things-end-on-video: rgba(/*[[end_bg]]*/);
    --hover-time-background: rgba(/*[[t_bg]]*/);
    --search-background-hover: rgba(/*[[sh_bg]]*/);
    --theme-radius: /*[[t_ra]]*/px;
    --theme-time-radius: /*[[tt_ra]]*/px;
    --theme-radius-big: /*[[t_rab]]*/px;
    --border-minus: calc(var(--border-width) * -1);
    --bg-border-minus: calc(var(--player-bg-border-width) * -1);
}
html:not(.style-scope)[dark], :not(.style-scope)[dark] {
    --yt-spec-brand-background-primary: var(--top-bar-and-search-background) !important;
    --yt-spec-brand-background-solid: var(--bg-color) !important;
    --yt-spec-general-background-a: var(--bg-color) !important;
    --yt-spec-call-to-action: var(--theme) !important;
    --yt-spec-badge-chip-background: var(--playlist-bg) !important;
    --yt-spec-text-primary: var(--text-color) !important;
    --yt-spec-text-secondary: var(--nd-text-color) !important;
}
.ytp-preview:not(.ytp-text-detail) .ytp-tooltip-text-no-title {
    display: block !important;
    background-color: var(--hover-time-background) !important;
}
ytd-live-chat-frame {
    transition: all .2s cubic-bezier(0, 1, 1, 1) !important;
}

/*[[thing_blur]]*/
  
.ytp-ce-expanding-overlay-background, .ytp-ce-playlist-count {
    background: var(--things-end-on-video) !important;
}
.sbdd_b {
    background: var(--top-bar-and-search-background) !important;
}
.sbfl_b, .sbsb_a, #container.style-scope.ytd-masthead {
    background: transparent !important;
}
.sbsb_d {
    background: var(--search-background-hover) !important;
}
.gsfs, .ytp-ce-channel-metadata {
    color: var(--text-color) !important;
}
#player, ytd-multi-page-menu-renderer {
    border-radius: var(--theme-radius-big) !important;
}
a.thumbnail > .ytcd-basic-item-large-image, ytcp-thumbnail-with-title, ytd-playlist-thumbnail, ytd-thumbnail, #thumbnail, .thumbnail-container.ytd-notification-renderer, .ytp-caption-segment, .sbdd_b, .ytp-ce-video, .ytp-ce-playlist, [aria-live="polite"], .ytp-tooltip-bg, .ytp-tooltip-text.ytp-tooltip-text-no-title, .branding-img.iv-click-target, .branding-context-container-inner, ytd-thumbnail-overlay-bottom-panel-renderer, .ytp-progress-list, .ytp-play-progress.ytp-swatch-background-color, .ytp-load-progress, .ytp-hover-progress.ytp-hover-progress-light, .ytp-gradient-bottom, .style-scope.ytd-subscribe-button-renderer, #container.ytd-playlist-panel-renderer, .header.ytd-playlist-panel-renderer, ytd-button-renderer.style-suggestive[is-paper-button] tp-yt-paper-button.ytd-button-renderer, ytd-live-chat-frame, .ytp-ce-playlist-count, .ytp-ce-expanding-overlay-background {
    border-radius: var(--theme-radius) !important;
}
a.thumbnail > .ytcd-basic-item-large-image, ytcp-thumbnail-with-title, ytd-playlist-thumbnail, ytd-thumbnail, #thumbnail, .thumbnail-container.ytd-notification-renderer, #avatar, #author-thumbnail.ytd-comment-simplebox-renderer, .style-scope.ytd-comment-renderer.no-transition, #player, .ytp-preview:not(.ytp-text-detail) .ytp-tooltip-text-no-title, #container.ytd-playlist-panel-renderer, ytd-live-chat-frame, ytd-thumbnail-overlay-side-panel-renderer, ytd-thumbnail-overlay-bottom-panel-renderer, .ytp-gradient-bottom {
    border-collapse: separate !important;
    overflow: hidden !important;
    box-shadow: var(--border-minus) 0 var(--border-color), 0 var(--border-width) var(--border-color), var(--border-width) 0 var(--border-color), 0 var(--border-minus) var(--border-color) !important;
}
.ytp-gradient-bottom {
    box-shadow: var(--bg-border-minus) 0 var(--border-color), 0 var(--player-bg-border-width) var(--border-color), var(--player-bg-border-width) 0 var(--border-color), 0 var(--bg-border-minus) var(--border-color) !important;
}
#text.ytd-channel-name, yt-button-renderer.yt-formatted-string.yt-button-renderer, paper-ripple, a.yt-simple-endpoint.yt-formatted-string, .style-scope.ytd-menu-renderer.force-icon-button.style-default-active {
    color: var(--theme) !important;
}
paper-ripple {
    border-radius: var(--theme-radius) !important;
    color: var(--theme) !important;
}
ytd-thumbnail-overlay-toggle-button-renderer, ytd-thumbnail-overlay-time-status-renderer, ytd-thumbnail-overlay-side-panel-renderer {
    border-radius: var(--theme-time-radius) !important;
    background-color: var(--hover-time-background) !important;
}
a.yt-simple-endpoint.yt-formatted-string::selection, span::selection, yt-formatted-string::selection {
    background: var(--theme) !important;
}
#container.ytd-searchbox, .yt-ui-ellipsis {
    background-color: transparent !important;
    border-color: transparent !important;
}
ytd-author-comment-badge-renderer[creator] {
    background-color: rgb(36, 36, 36) !important;
}
ytd-playlist-thumbnail, ytd-thumbnail, ytd-compact-playlist-renderer, ytd-compact-video-renderer, ytd-compact-radio-renderer, ytd-compact-playlist-renderer>div>div>div>a, ytd-compact-video-renderer>div>div>div>a, ytd-compact-radio-renderer>div>div>div>a {
    transition: all 200ms ease-in-out !important;
    transition-timing-function:cubic-bezier(0.42, 0, 1, 1);
}
ytd-compact-playlist-renderer:hover>div>ytd-playlist-thumbnail, ytd-compact-video-renderer:hover>div>ytd-thumbnail, ytd-compact-radio-renderer:hover>div>ytd-thumbnail {
    box-shadow: var(--border-minus) 0 var(--border-hover-color), 0 var(--border-width) var(--border-hover-color), var(--border-width) 0 var(--border-hover-color), 0 var(--border-minus) var(--border-hover-color) !important;
}
ytd-compact-playlist-renderer:hover, ytd-compact-video-renderer:hover, ytd-compact-radio-renderer:hover {
    margin-inline-start: -15px !important;
}
ytd-compact-playlist-renderer:hover>div>div>div>a, ytd-compact-video-renderer:hover>div>div>div>a, ytd-compact-radio-renderer:hover>div>div>div>a {
    margin-inline-end: 15px !important;
}
ytd-compact-playlist-renderer:active>div>ytd-playlist-thumbnail, ytd-compact-video-renderer:active>div>ytd-thumbnail, ytd-compact-radio-renderer:active>div>ytd-thumbnail {
    box-shadow: -2px 0 var(--border-click-color), 0 2px var(--border-click-color), 2px 0 var(--border-click-color), 0 -2px var(--border-click-color) !important;
}
.ytp-chrome-top, .ytp-chrome-bottom, .ytp-gradient-bottom {
    transition: all .2s cubic-bezier(0, 1, 1, 1) !important;
}
.ytp-autohide:not(.ytp-autohide-active) .ytp-gradient-top, .ytp-autohide:not(.ytp-autohide-active) .ytp-gradient-bottom {
    display: block !important;
}
.ytp-gradient-bottom {
    height: 5px !important;
    background-image: none !important;
    background-color: var(--in-player-bg-color) !important;
}
.ytp-gradient-top[aria-hidden=true], .ytp-gradient-bottom[aria-hidden=true], .ytp-autohide .ytp-gradient-top, .ytp-autohide .ytp-gradient-bottom, .ytp-autohide .ytp-playlist-menu-button, .ytp-autohide .ytp-back-button, .ytp-autohide .ytp-title-channel, .ytp-autohide .ytp-title, .ytp-autohide .ytp-chrome-top .ytp-watch-later-button, .ytp-autohide .ytp-chrome-top .ytp-share-button, .ytp-autohide .ytp-chrome-top .ytp-copylink-button, .ytp-autohide:not(.ytp-cards-teaser-shown) .ytp-cards-button, .ytp-autohide .ytp-overflow-button, .ytp-autohide .ytp-chrome-bottom, .ytp-chrome-top[aria-hidden=true], .ytp-chrome-bottom[aria-hidden=true] {
    margin-block-start: 50px !important;
    margin-block-end: -50px !important;
    transition: all .2s cubic-bezier(1, 0, 1, 1) !important;
}
}
