/* ==UserStyle==
@name           Brick Hill+
@namespace      USO Archive
@author         arm
@description    `Made to resemble the scrapped site design.`
@version        20210630.0.10
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */

@-moz-document url-prefix("https://www.brick-hill.com/") {
.card .top:not(.absolute).blue {
    color: #fff;
    background: linear-gradient(0deg, #253e6f 0%, #436099 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.card .top:not(.absolute),
.thread-label.blue {
    color: #fff;
    background: linear-gradient(0deg, #253e6f 0%, #436099 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

nav .info {
    color: #fff;
    background: linear-gradient(0deg, #267ab900 0%, #4abef900 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.card .top:not(.absolute).green, .thread-label.green {
    color: #fff;
    background: linear-gradient(0deg, #157945 0%, #2fbb72 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.card .top:not(.absolute).red,
.button:not(.plain):not([disabled]).red,
button:not(.plain):not([disabled]).red,
.thread-label.red {
    color: #fff;
    background: linear-gradient(0deg, #820a17 0%, #aa2230 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}


.card .top:not(.absolute).orange, .thread-label.orange {
    color: #fff;
    background: linear-gradient(0deg, #8e6022 0%, #d7a053 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.notif,
.nav-notif,
.alert.error {
    color: #fff;
    background: linear-gradient(0deg, #820a17 0%, #aa2230 100%);
    font-weight: 600;
    border: 0;
}


nav div.secondary,
footer {
    color: #fff;
    background: linear-gradient(0deg, #242424 0%, #262626 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

nav div.primary {
    background: linear-gradient(0deg, #1e242d 0%, #353c46 100%);
}

textarea {
    border-radius: 3px;
}













.tabs .tab {
    border-radius: 0px 0px 0px 0px;
}



nav div ul li {
    padding-right: 3px
}
}

@-moz-document url-prefix("https://www.brick-hill.com/") {
.button.blue,
button.blue,
button:not(.plain):not([disabled]).blue {
    color: #fff;
    background: linear-gradient(0deg, #253e6f 0%, #436099 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0;
}

button.green,
.button:not(.plain):not([disabled]).bucks,
.button:not(.plain):not([disabled]).green,
button:not(.plain):not([disabled]).bucks {
    color: #fff;
    background: linear-gradient(0deg, #157945 0%, #2fbb72 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0;
}

button.orange,
button:not(.plain):not([disabled]).bits,
.button:not(.plain):not([disabled]).orange {
    color: #fff;
    background: linear-gradient(0deg, #8e6022 0%, #d7a053 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0;
}

button.small {
    color: #fff;
    background: linear-gradient(0deg, #9a9a9a 0%, #cbcbcb 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0;
}

.profile-card.award img {
    border: 0px;
}



.crate-types .active,
.crate-types :hover {
    border-left: 0px;
    background-color: #3f4244;
    transition: color .1s linear;
}
}
