.marketSidebarMenu a.selected {
    box-shadow: inset 2.5px 0 0 0 rgb(130 88 248);
}

.marketSidebarMenu a:hover path, .marketSidebarMenu a:hover line, .marketSidebarMenu a.selected path, .marketSidebarMenu a.selected line {
    stroke: rgb(130,88,248);
}

.marketSidebarMenu a:hover .alt path, .marketSidebarMenu a.selected .alt path {
    fill: rgb(130,88,248);
    stroke: none;
}

.numberStyle, .likeNodes .node .counter {
    color: var(--accent);
    text-shadow: 1px 0px 7px rgb(137 95 255), 0px -2px 1px rgb(0 0 0 / 0%);
}

.page_counter .count {
    color: var(--accent);
    text-shadow: 1px 0px 7px rgb(137 95 255), 0px -2px 1px rgb(0 0 0 / 0%);
}

.navTabs .navTab.PopupClosed .navLink:hover .counter-container img,
.navTabs .navTab.PopupOpen .navLink .counter-container img {
    filter: hue-rotate(var(--accent-hue));
}

.forumSearchThreads--Link--Icon {
    border-radius: 2px;
    padding: 0 14px;
    background: rgb(34,142,93);
    color: rgb(234 234 234);
    line-height: 30px;
    height: 30px;
    display: inline-block;
    font-size: 14px;
}

.navTabs .navTab.PopupOpen .navLink {
    background: var(--accent-dark);
}

.navigation-header {
    background: var(--accent-dark2);
    border-bottom: none;
}

.messageStateIcon {
    color: var(--accent) !important;
}

.conversationItem:hover,
.conversationSearch--Recipient:hover {
    background: var(--accent-dark);
}

#AlertsMenu_Counter {
    filter: none;
    border: none;
    background: var(--accent);
    box-shadow: 0 0 5px 2px var(--accent);
}

.Menu {
    border: none;
    background: var(--accent-dark2);
}

.account-menu-sep {
    visibility: hidden;
}

#AccountMenu > a:hover {
    background: var(--accent-dark);
}

/******************** Profile ********************/

.nodeList .node.current > .nodeInfo > .nodeText > .nodeTitle > a, .nodeList .node .current>div>.nodeTitle>a {
    background: rgb(45, 45, 45);
    color: rgb(137, 95, 255);
    font-weight: 600;
}

