    filter: hue-rotate(var(--accent-hue));
}

.node h3.nodeTitle > a:hover {
    background: var(--accent-dark3);
}

.monthMaecenas .donationAmount {
    color: var(--accent);
}

.monthMaecenas--quote {
    background: var(--accent-dark);
}

.monthMaecenas--quote:before {
    border-bottom: 10px solid var(--accent-dark);
}

/* Chat */
#ChatboxStartIcon {
    background: var(--accent-dark2);
}

#ChatboxStartIcon:before {
    color: var(--accent);
}

#completeChatbox.chatboxWidget .header {
    background: var(--accent);
}

.chatboxWidget .chatboxWrapper {
    border: 1px solid var(--accent-dark);
}

#chatbox .meMessage {
    filter: hue-rotate(var(--accent-hue));
}

#chatbox {
    background: var(--accent-dark2);
}

#completeChatbox.chatboxWidget .controls.reversedDirection {
    border-top: 1px solid var(--accent-dark);
}

.ChatboxInput {
    background-color: var(--accent-dark2) !important;
}

#completeChatbox .controls.reversedDirection {
    background: var(--accent-dark2);
    border-top: 1px solid var(--accent-dark);
}

#completeChatbox.chatboxWidget .header>.element.closeChatboxButton {
    padding: 0px 8px;
    margin: 0 -8px;
    font-size: 14px;
    color: var(--accent-whiter);
    text-shadow: 0px 0px 0px rgb(54, 54, 54), 1px 1px 1px var(--accent-dark2);
    cursor: pointer;
}


/******************** Forums ********************/
.titleBar h1,
#pageDescription {
}

.pageNavLinkGroup {
    display: flex;
    margin: 0;
    flex-direction: column-reverse;
    background: var(--accent-dark2);
}

.pageNavLinkGroup .linkGroup {
    padding-bottom: 10px;
    align-self: start;
}

.PageNav {
    padding: 0px 0px 15px;
}

.PageNav a.currentPage {
    background: var(--accent);
}

.PageNav a.currentPage:hover {
    background: var(--accent-light) !important;
}

.stickyThreads {
    border-left: 2px solid var(--accent);
}

.nodeList .node .current > div > .nodeTitle > a {
    color: var(--accent-light);
}

/******************** Threads ********************/
.thread_view .titleBar,
.messageList .message,
.simpleRedactor.QuickReplyRedactor{
    background: var(--accent-dark2);
}

.pageNavLinkGroup {
    border: none;
}

.nodeList .node.current > .nodeInfo > .nodeText > .nodeTitle, .nodeList .node .current>div>.nodeTitle {
    color: rgb(130, 88, 248);
}

#pageDescription > a,
#pageDescription > a > abbr {
    color: var(--accent);
}

.forumSearchThreadsMenu {
    background: var(--accent-dark2);
}

.forumSearchThreads--Link--Icon {
    background: var(--accent);
}

.forumSearchThreads--Link:hover .forumSearchThreads--Link--Icon {
    background: var(--accent-light);
}

.forumSearchThreadsMenu .discussionListItem.current {
    background: var(--accent-dark);
}

.message .messageSimple .comment {
    background: var(--accent-dark2);
}

.messageSimple .comment .commentContent .author {
    color: white;
    background: var(--accent);
}

.alertNotice {
    background: var(--accent-dark2);
}

.CommentLoader {
    color: var(--accent);
}

html .redactor_toolbar li a:hover {
    background: var(--accent-dark);
}

.needInNewArticles {
    filter: hue-rotate(var(--accent-hue));
}

/******************** Articles ********************/
#articlesGrid, .articleImageBox {
    background: var(--accent-dark2);
}

#articleTitleBlock {
    background: var(--accent-dark);
}

.TagContainer {
    border: 0;
    padding: 5px;
    margin: 0;
    background: var(--accent-dark2);
}

.tagList .tag {
    background: var(--accent);
}

#lzt-market-logo {
    background: url(https://i.imgur.com/hAfLKgW.png) no-repeat center;
    background-size: 100%;
}

.marketUpgradeBadge::before {
    content: '';
    background: url(https://i.imgur.com/hAfLKgW.png) no-repeat center;
    background-size: 100%;
    width: 100px;
    height: 30px;
    float: left;
}

/******************** Market ********************/
#lzt-market-logo {
    filter: hue-rotate(var(--accent-hue));
}

.backToHomeButton {
    background: var(--accent-dark);
}

.backToHomeButton:hover {
    background: var(--accent-dark2);
}

.marketSidebarMenu {
    background: var(--accent-dark2);
}

.marketSidebarMenu .svgIcon svg {
    filter: hue-rotate(var(--accent-hue));
}

.chosen-container .chosen-results li.active-result,
.marketSidebarMenu a:hover {
    background: var(--accent-dark);
