.section_head, #profile_page .section, #profile_page .username, .theme-titles h2, 
.customize-wrapper h2, .theme-selector-wrapper h2, .layout-selector-wrapper h2 {
border-width: 1px 0 1px 0!important;
margin: 10px 0!important;
padding: 5px 0 0 5px!important;
}
.section_head span.view_options { float: right; } /* section header text move to right */
.warningbar { padding: 8px; border-radius: 8px; }
div.tips-box { border-radius: 8px; }
div.form-basic.form-fix { border-width: 2px!important; } /* from manage accounts > lost info */

#content div[style*="border-bottom: 1px solid #6699cc"] { border-width: 0 0 1px 0!important; } /* user boxes in search by interest result*/

