css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("whirlpool.net.au") {
  #root, #footer {
   width: 100% !important;
  }

  /* New style changes 10/04/2015 */
  #page, #topbar {
    width: 100% !important;
  }

  /* This shifts the logo to be within the sidebar on the left (no overflow into the top bar) */
  /*
  #logo h1 {
    margin: 0 !important;
  }
  
  #left.sidebar {
    top: 160px !important;
  }
  */
  
  /* Oops, 13px was the original font size of the old theme, not 12px */
  #replylist div.reply div.replytext {
    font-family: Verdana, "Helvetica Neue", Helvetica, "Open Sans",sans-serif !important;
    font-size: 13px !important;
  }
  
  #replylist div.replyform div.editor div.textarea textarea {
    font-family: Verdana, "Helvetica Neue", Helvetica, "Open Sans",sans-serif !important;
    font-size: 13px !important;
  }
  
  #replylist div.reply div.replymeta div.replyuser div.voteblock {
    font-family: "Courier New", Consolas, "Lucida Console", monospace !important;
  }
  
  #threads table tbody tr td.title a.group {
    float: right;
  }
  
  /*
  #threads table tbody tr td.title a.title {
   font-family: "Helvetica Neue",Helvetica,Arial,sans-serif !important;
  }*/
  
  @media (min-width: 1000px) {
    /* Shift the quote button to right column */
    #replylist div.reply div.quote {
      top: 0 !important;
      bottom: auto !important;
      left: auto !important;
      right: 0 !important;
    }

    #replylist div.reply div.quote div.quote-inner {
      background: none !important;
    }
  }
}
