css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

/* Old Twitter */
#recommended_users, #trends, 
div#following, div#following + hr,
#side_lists p + hr,
.promotion, .no-lists  { display: none !important; }

/* New Twitter */
div.dashboard .following-activity, div.dashboard .new-followers-activity,
div.dashboard .following-activity + hr, div.dashboard .new-followers-activity + hr,
div.dashboard .wtf-links, div.dashboard .wtf-links + hr,
div.dashboard .getting-started, div.dashboard .getting-started + hr,
div.dashboard .contact-import-inner,
div.dashboard .trends-inner, div.dashboard .trends-inner + hr, 
div.route-home div.dashboard .user-rec-inner, 
div.route-home div.dashboard .user-rec-inner + hr,
div.dashboard .promo,
li.WtfLargeCarouselStreamItem,
div.Footer-adsModule,
div.gain_followers_prompt { display: none !important; }

div.route-profile .user-rec-inner,
div.route-profile .user-rec-inner + hr { display: block !important; }

/* Newer Twitter */
.wtf-module, .trends  { display: none !important; }

}
