image#page-proxy-favicon, box.unknownIdentity#identity-box, stack#page-proxy-stack, searchbar#searchbar textbox, textbox#urlbar, #PersonalToolbar, #personal-bookmarks, tabs.tabbrowser-tabs#tabbrowser#tabs, #content,#appcontent,#content > tabbox > tabpanels {
     background-color: transparent!important;
     border-style: none!important;
     color: #81F0F6!important}
searchbar#searchbar textbox:hover, #urlbar:hover{
     background-color: rgba(129, 240, 246, 0.2)!important;
     border-style: none!important;}
#web-developer-toolbar, #web-developer-toolbar toolbarbutton{
     background-color: transparent!important;
     border-style: none!important;
     color:#FFFFFF!important;
     text-shadow: #81F0F6 0px 0px 2px !important;}
.tabbrowser-tab, .tabs-newtab-button {
	 background: transparent !important;
	 text-shadow: #FFF 0px 0px 2px !important;
	 font-size: 0.9em !important;
	 box-shadow: inset 0px 0px 1em 0px rgba(255,255,255,.5) !important; 
	 height: 24px !important;
border:none !important;}
.tabs-newtab-button{margin-left: 1px !important;}
.tabbrowser-tab:not([pinned="true"]){
	 margin-left: 1px !important;
     color: #FFFFFF !important;}
.tabbrowser-tab[selected="true"]{
	 background: transparent !important;
	 color: #FFFFFF !important;
         text-shadow: #81F0F6 0px 0px 2px !important;
	 box-shadow: inset 0px 0px 10px 0px #81F0F6 !important;}	
.tabbrowser-tab:hover{
	background: transparent !important;
	color: #81F0F6 !important;
	text-shadow: #555444 0px 0px 2px !important;
	}
#browser-bottombox, #status-bar, #status-bar > *, #nav-bar, #PersonalToolbar, #addon-bar, #main-window[tabsontop="false"] #TabsToolbar:nth-last-child(1n+2),
    #main-window[tabsontop="false"] #PersonalToolbar[collapsed="false"] + #TabsToolbar,
    #main-window[tabsontop="false"] [id^="__customToolbar"]:not([collapsed="true"]) ~ #TabsToolbar {
	 background: transparent !important;
	 color: #FFFFFF !important;
	-moz-appearance: none !important;
}
.chromeclass-directories *, #bookmarksPanel * {
    color: #FFFFFF !important;
    text-shadow: #81F0F6 0px 0px 2px !important;}
#main-window[inFullscreen="true"]{
    -moz-appearance: -moz-win-borderless-glass !important;
    background: none !important;}
#main-window[stylish-url="about:blank"]:not([onclose^="PrintUtils"]):not([title^="View Source Chart"]) #content browser:not([src]){ 
    opacity: 0 !important;}
        /* Google Toolbar */
#gtbToolbar{ background-color: transparent !important;}
        /* Identitybutton */
#identity-box.unknownIdentity { 
    opacity: 0.5 !important;
    background: transparent!important;}
#identity-box.unknownIdentity:hover { 
    opacity: 1 !important;
    background-color: rgba(129, 240, 246, 0.2)!important;}


