css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#main-window[sizemode="maximized"] #toolbar-menubar[autohide="true"]
{
 overflow: visible !important;
 position: fixed;
 z-index: 2;
 margin-top: -1.5em;
 margin-left: /*[[lng]]*/;
 background: none !important;
 }
#main-window[sizemode="maximized"] #toolbar-menubar[autohide="true"]:hover
,#main-window[sizemode="maximized"] #toolbar-menubar[autohide="true"]:focus
,#main-window[sizemode="maximized"] #toolbar-menubar[autohide="true"]:active
{
 margin-top: -1px;
 margin-left: 0;
}

#main-window[sizemode="maximized"] #toolbar-menubar[autohide="true"] #main-menubar
{
 background: -moz-linear-gradient(rgb(247,182,82), rgb(215,98,10) 95%);
 border-radius: 0 0 4px 4px !important;
 border: 1px solid rgba(83,42,6,.9) !important;
 border-top: none !important;
 box-shadow: 0 1px 0 rgba(255,255,255,.25) inset, 0 0 0 1px rgba(255,255,255,.25) inset;
}

#main-window[sizemode="maximized"] #toolbar-menubar[autohide="true"] #main-menubar > menu
{
 margin-top: -1px;
 color: white !important;
 text-shadow: 0 0 1px rgba(0,0,0,.7), 0 1px 1.5px rgba(0,0,0,.5); 
}
