	/* inheritance! */
	*{
		cursor:inherit!important;
		font-family:inherit!important;
	}
	/* new cursor (grey) & give me a new font family first & some more added */
	html{
		font-family:"Lucida Sans Unicode","Lucida Grande",FreeSerif,Tahoma,Verdana,Kalimati,Arial,Helvetica,FreeSans,Sans-serif!important;
		cursor:/*[[cursor_main]]*/auto!important;
	}
	/* new cursor (green) */
	input,
	a{
		cursor:/*[[cursor_libu]]*/pointer!important;
	}
	/* new cursor (orange) */
	input[type="file"],
	input[type="text"],
	textarea,
	select{
		cursor:/*[[cursor_edits]]*/auto!important;
	}
	/* new cursor (blue) */
	*.forms_txt_fade,
	*.forms_txt_off{
		cursor:/*[[cursor_fade]]*/auto!important;
	}
	/* new cursor (red) */
	*.forms_btn_off,
	*.forms_btn_view{
		cursor:/*[[cursor_disabled]]*/auto!important;
	}
	/* new cursor (yellow) */
	a[href*="../"]{
		cursor:/*[[cursor_map]]*/pointer!important;
	}
	/* damn the map! */
	#view > div{
		overflow:visible;
	}
	#view * a > img[width="41"][height="41"],
	#view * a > img[width="20"][height="20"]{
		overflow:visible;
		left:-1px!important;
		top:-1px!important;
	}
	#view * a > img[width="41"][height="41"]{
		height:43px!important;
		width:43px!important;
	}
	#view * a > img[width="20"][height="20"]{
		height:22px!important;
		width:22px!important;
	}
	/* the links */
	a,
	a:visited{
		color:#B3DF00!important;
		padding:0px 2px;
		border:1px solid transparent;
	}
	a[href*="user_info"]{
		color:#F8A406!important;
	}
	a[href*="faction_info"]{
		color:#AB82FF!important;
	}
	/* special login button */
	input.forms_btn[name="login"]{
		background-color:#010101!important;
		color:#C0C0C0!important;
		-moz-box-shadow:0 0 2px 1px #8DAF00;
		-webkit-box-shadow:0 0 2px 1px #8DAF00;
	}
	/* text areas & text input fields get a new style and slightly rounded corners */
	*.forms,
	input.forms_txt{
		background-color:#010!important;
		color:#ccc!important;
		padding:2px;
	}
	/* text input fields get automatic height in addition */
	input.forms,
	input.forms_txt,
	/* file upload (symbols) fields get it too */
	input[type="file"]{
		height:auto!important;
	}
	/* amount fields */
	input[name="amount"],
	input[name="price"]{
		padding:0px 1px;
		text-align:right!important;
		height:15px!important;
	}
	/* planet thing buttons */
	input[value="Sell"],
	input[value="Buy"],
	input[value="Accept"],
	input[value="Hire"]{
		margin:2px 0px 0px 0px!important;
		padding:0px 0px 0px 0px!important;
		height:auto!important;
	}
	/* Planet Terminal display hack */
	td[width="55%"],
	td[width="45%"]{
		float:left;
		width:100%!important;
	}
	td[width="45%"]{
		margin:2em 0px 0px 0px;
	}
	td[width="10"]{
		display:none;
	}
	/* ship info */
	*.forms_txt_fade,
	*.forms_txt_off{
		margin:1px;
	}
	/* ship info fields get blue shadow on hovering */
	*.forms_txt_fade:focus,
	*.forms_txt_fade:hover,
	*.forms_txt_off:focus,
	*.forms_txt_off:hover,
	/* nicer ship pages script support */
	td.forms_txt_fade:focus,
	td.forms_txt_fade:hover{
		-moz-box-shadow:0 0 2px 1px #39f;
		-webkit-box-shadow:0 0 2px 1px #39f;
	}
	/* active buttons get rounded corners */
	*.forms_btn,
	/* drop downs get rounded corners */
	select.forms,
	/* drop downs need padding */
	select.forms{
		padding:1px;
	}
	/* deactivated buttons and those who bring you just to the sector view
	 * get rounded corners & the same style */
	*.forms_btn_off,
	*.forms_btn_view{
		border:1px solid #404040!important;
		color:#404040!important;
	}
	/* make the price search & Edit buttons stand out */
	input[value="E"],
	input[value="S"]{
		color:#C0C0C0!important;
		padding:0px 2px 1px 3px;
		width:22px!important;
		text-align:center!important;
	}
	/* new navigation computer spacing */
	input[style*="width:"][style*="22px;"]{
		margin:1px!important;
	}
	input[value*="Computer"],
	input[value*="Scan "]{
		margin:1px!important;
		width:102px!important;
	}
	/* give the the links a different colour when hovering */
	a:focus,
	a:hover,
	a:active{
		color:#F8A406!important;
		border:1px solid #9D9DA1;
	}
	/* give the the buttons a different colour & shadow when hovering */
	*.forms_btn:focus,
	*.forms_btn:hover,
	*.forms_btn:active{
		color:#F8A406!important;
		-moz-box-shadow:0 0 2px 1px #F8A406;
		-webkit-box-shadow:0 0 2px 1px #F8A406;
	}
	/* give deactivated buttons on hovering a red shadow & font */
	*.forms_btn_off:focus,
	*.forms_btn_off:hover,
	*.forms_btn_off:active,
	*.forms_btn_view:focus,
	*.forms_btn_view:hover,
	*.forms_btn_view:active{
		color:#f33!important;
		-moz-box-shadow:0 0 2px 1px #f33 ;
		-webkit-box-shadow:0 0 2px 1px #f33 ;
	}
	/* remove hover effect for 16x16 image links
	 * when found inside a properly formatted cell
	 */
	td[width="16"]>a[href*="user_info"]:focus,
	td[width="16"]>a[href*="user_info"]:hover,
	td[width="16"]>a[href*="user_info"]:active{
		color:transparent!important;
		background-color:transparent!important;
		border:1px solid transparent;
	}
	/* remove all borders from inside the main table */
	td[style*="border-style"]{
		border-style:none!important;
	}
	/* make new ion trail popup boxes fit the design */
	.infobox{
		padding:0.5em!important;
		width:20em!important;
		height:15em!important;
	}
	/* jumpbox fix (used in some of my scripts) */
	input.forms_txt_jboff{
		color:#606060!important;
	}
	input.forms_txt_jbon{
		color:#ccc!important;
	}
	input.forms_txt_jbact{
		color:#3f3!important;
