	/* create exception for map links */
	a[href*="../index.php"]:focus,
	a[href*="../index.php"]:hover,
	a[href*="../index.php"]:active{
		display:block;
		width:100%!important;
		height:100%!important;
		border:none!important;
		-moz-border-radius:0px;
		-webkit-border-radius:0px;
		margin:0px!important;
		padding:0px!important;
		color:transparent!important;
		background-color:#f00!important;
		opacity:0.2;
	}
	/* board titles */
	td[bgcolor="#1b1b1b"][colspan="5"]{
		background-color:#101010!important;
		font-size:12px!important;
	}
	td[bgcolor="#1b1b1b"][colspan="5"]>a{
		background-color:transparent!important;
		width:80%!important;
		display:inline-block!important;
		margin:0px 5px!important;
		border:1px solid transparent;
		float:none!important;
		color:#C0C0C0!important;
		font-variant:small-caps;
		letter-spacing:0.2em;
	}
	td[bgcolor="#1b1b1b"][colspan="5"]>a:focus,
	td[bgcolor="#1b1b1b"][colspan="5"]>a:hover,
	td[bgcolor="#1b1b1b"][colspan="5"]>a:active{
		color:#F8A406!important;
		border:1px solid #9D9DA1;
	}
