css:
/* Version 1.0.004 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
  url-prefix("http://www.tignes.net/fr/ski-a-tignes/plan-des-pistes-interactif")
 ,url-prefix("http://www.tignes.net/en/skiing-in-tignes/interactive")
 ,url-prefix("http://www.tignes.net/fr/infos-pratiques/plan-de-la-station")
 ,url-prefix("http://www.tignes.net/en/practical-info/resort-map")
 ,url-prefix("http://www.tignes.net/fr/ski-a-tignes/webcams")
,url-prefix("http://www.tignes.net/en/skiing-in-tignes/webcams")
 {

div#page
 {width: 100% !important;
  padding: 0 !important;
 }
div#entete {
  padding: 0 !important;
 }
#entete-container {
  height: 42px !important;
 }
#entete ul#entete-languages {
  clear: none!important;
  position: relative !important;
  top: 3px !important;
 }
#entete-titles p {
  font-size: 18px !important;
  position: absolute !important;
  right: 1px !important;
  top: 44px !important;
 }
/* #menu {clear: left !important;} */
#menu ul#nav-menu li a {
  padding-left: 10px !important;
  padding-right: 10px !important;
 }
 
#meteo-bandeau {
  height: 25px !important;
 }
#meteo-date {
 clear: left !important;
 }

div#homeMea
,div#homeAltFlash
 {width: 100% !important;
  padding: 0 !important;
 }

/* Flash player */
embed#main {
  width: 100% !important;
  height: /*[[FlashPlayerHeight]]*/ !important;
 }

div#homeContenus {
  width: auto !important;
  padding: 0 !important;
 }
div#planHome {
  width: 50% !important;
 }
#homeInterneDroite {
  float: right !important;
 }
.blocDroiteDroite {
  float: right !important;
 }
#footer {
  padding: 0 !important; 
 }
#page-footer {
  background: none !important;
 }

/* Webcams */
#homeSecu {
  width: auto !important;
 }
#secuZoneFlash {
  width: auto !important;
  clear: left !important;
 }
#homeSecu p {
  width: auto !important;
 }   
#webcam1
,#webcam2 {
  width: auto !important;
 }
/* Flash player */
embed#plugin {
  width: 100% !important;
 }   
}
