css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("my.deviantart.com"), domain("www.deviantart.com") {

#messages .birthday-user .avatar,
#messages .birthday-user .icon { display: none !important; }

#messages .birthday-user { padding-left: 16px !important; height: 40px !important;  }
#messages .birthday-user span.birthday-when { font-size: 10px !important; }

#messages .birthday-user .smbutton { top: -15px !important; right: -90px !important; width: 50px !important; min-width: 50px !important; height: 16px !important; max-height: 16px !important; min-height: 16px !important; padding: 0 !important; }
#messages .birthday-user .smbutton span { margin: 0 !important; font-size: 10px !important; padding: 0px 5px !important; }
#messages .birthday-user .smbutton span img { top: 7px !important; right: 8px !important; }

}
