css:
@-moz-document url('chrome://browser/content/browser.xul') {

/*Rounded corners for tab*/
tab { -moz-border-radius: /*[[rad]]*/ !important; }

/*Glossy look for bottom bar and tab*/
#TabsToolbar, tab { 
/*[[tabcol]]*/

/*[[glassy]]*/
}

/*Background area behind the tabs*/
.tabbrowser-tabs { /*[[tabback]]*/ }


/*Tab text color/font*/
.tab-text {
/*[[tabtext]]*/
font-weight: normal !important;
font-family: Verdana !important;}

/* Background for toolbars */
.chromeclass-toolbar, .chromeclass-directories, .chromeclass-menubar, #navigator-toolbox
{  /*[[toolback]]*/
   background-image: none !important; 
   /*[[glassy]]*/ }


/*[[tooltext]]*/


/* Text color for Statusbar */
.toolbarbutton-text, #browser-bottombox
	{ /*[[statusbar]]*/ }

/* Background color for Statusbar */
#browser-bottombox, #addon-bar, #downbarHolder, #FindToolbar
{  -moz-appearance: none !important; 
   /*[[statback]]*/
   background-image: none !important; 
   /*[[glassy]]*/ }


}
