css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("irccloud.com") {
#buffer div.scroll div.messageRow span.message {
  font-family: /*[[fontface]]*/;
  font-size: /*[[size]]*/; 
}

#buffer div.scroll span.date {
  font-family: /*[[fontface]]*/;
}

#buffer div.scroll span.author {
  width: 100px;
  font-family: /*[[fontface]]*/;
  font-size: /*[[size]]*/;
}

#users {
  width: 100px;
  font-family: /*[[fontface]]*/ !important;
  font-size: /*[[size]]*/ !important;
}

textarea {
  font-family: /*[[fontface]]*/ !important;
}

span#nickname a.selectedNickname {
  font-family: /*[[fontface]]*/;
}
/*#sidebar #limits {
  display: none !important;
}*/
}
