@-moz-document domain('google.com'), domain('google.se'), domain(google.fr), domain(google.it), domain(google.es), domain(google.co.uk), domain(google.no), domain(google.ca), domain(google.de) {

#gb>div {
  background-color: /*[[bordercolor]]*/ !important;
  border-color: #ffffff !important;
}
#gb * {
  color: /*[[textcolor]]*/ !important;
}
#gb a:hover * {
  background-color: white !important;
  color: black !important;
}

}
