@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.tppc.info") {
/** Link Coloring **/
    /** General Links **/
a, legend {
    color:#60c860 !important
}

a:hover {
    color:#80ee80 !important
}
    /** White Links **/
.vbmenu_popup a,
.vbmenu_control a,
.vbmenu_option a,
.vbmenu_option_alink a,
.thead a,
.tfoot a,
.tcat a{
    Color:#ffffff !important
}
/** Menues & Table Headers **/
.tcat,
.vbmenu_control {
    background-image:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAoCAYAAAF IsaDhAAAABGdBTUEAALGPC/xhBQAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAALEwAACxMBAJqcGAAAAAd0SU1FB9sHEg olMtGuAfkAAAAZdEVYdENvbW1lbnQAQ3JlYXRlZCB3aXRoIEdJ TVBXgQ4XAAAAeUlEQVQI102MyQ3CUBBDH27iF0MDFJAOoBEaQZ THKcqqECYzw4EfBR8sW14opaTO9wvqugEt44zSE5rHNWXzgtp2 QLauKD2ged5S8TGUlig2R/04ITNDboYARJzQ+9XVBYDS/Xev9PhXAOrnqdpt81qO2NODjgUV+QUqklTfdsDOlQAAAABJRU5 ErkJggg==') !important;
    background-position:center !important;
}

/** Banner **/
    /**Green Mudkip - https://i.imgur.com/r67CGBL.png**/
    /**Bulbasaur - https://i.imgur.com/TPrmMrl.png**/

img[src="bb_images/kife/version2/misc/banner1.jpg"] {
    width:0px !important;
    height:120px !important;
    padding-right:791px !important;
    background-image:url('/*[[banner]]*/') !important;
    border-radius:500px 0px 0px 500px !important
}

table[style="background-image:url(/bb_images/kife/version2/misc/banner2.jpg)"] {
    background-image:url('https://i.imgur.com/77amYCs.png') !important;
    border-radius:500px !important;
    border:5px solid #565656 !important;
}

/** Post Icons **/
    /** New Post icon **/
img[src="bb_images/kife/version2/statusicon/forum_new.gif"] {
    width:0px !important;
    height:32px !important;
    padding-right:32px !important;
        /**if you want a different pokemon as the new posts icon on the index then change "001" below to that pokemon's number**/
    background-image:url('http://forums.tppc.info/images/pokemon/001.gif') !important
}

/** Background **/
body {
    background-image:url('/*[[BG]]*/') !important;
    background-attachment:fixed !important;
    margin-left:30px !important;
    margin-right:30px !important;
}

body > div > div {
    background-color:transparent !important
}

.page {
    background-color:transparent !important
    }

/** Pagenav Mods **/
div.pagenav table {
    background-color:transparent !important;
    
}

div.pagenav td {
    border-radius:15px 0px 15px 0px !important;
    border: 1px solid #565656 !important;
}

}
