css:
@-moz-document url-prefix('http://userstyles.org/styles/51626') {
/*[[preview-mode]]*/
#style-options > LI:nth-child(1):after {
  content: "/*[[text-image-name]]*/" !important;/*Text*/
}
#style-options > LI:nth-child(2):after {
  content: "/*[[photo-image-name]]*/" !important;/*Photo*/
}
#style-options > LI:nth-child(3):after {
  content: "/*[[quote-image-name]]*/" !important;/*Quote*/
}
#style-options > LI:nth-child(4):after {
  content: "/*[[link-image-name]]*/" !important;/*Link*/
}
#style-options > LI:nth-child(5):after {
  content: "/*[[chat-image-name]]*/" !important;/*Chat*/
}
#style-options > LI:nth-child(6):after {
  content: "/*[[audio-image-name]]*/" !important;/*Audio*/
}
#style-options > LI:nth-child(7):after {
  content: "/*[[video-image-name]]*/" !important;/*Video*/
  content: "" !important;
}
#style-options > LI:first-child:before {
  background-image: 
  /*[[universal-prefix]]*//*[[text-image-name]]*//*[[universal-suffix]]*/,
  /*[[universal-prefix]]*//*[[photo-image-name]]*//*[[universal-suffix]]*/,
  /*[[universal-prefix]]*//*[[quote-image-name]]*//*[[universal-suffix]]*/,
  /*[[universal-prefix]]*//*[[link-image-name]]*//*[[universal-suffix]]*/,
  /*[[universal-prefix]]*//*[[chat-image-name]]*//*[[universal-suffix]]*/,
  /*[[universal-prefix]]*//*[[audio-image-name]]*//*[[universal-suffix]]*/,
  /*[[universal-prefix]]*//*[[video-image-name]]*//*[[universal-suffix]]*/ !important;
}
}

@-moz-document url-prefix('http://www.tumblr.com') {

/*UNIVERSAL*/
/*clip outline*/
#new_post:before {
  position: absolute;
  content: "";
  display: block;
  top: 4px;
  left: 20px;
  width: 500px;
  height: 75px;
  outline: solid thin red;
}
/*icons*/
#new_post a[class="new_post_label"] span {
  background-repeat: no-repeat !important;
  margin-left: 12px;
}
/*font*/
#new_post a[class="new_post_label"] {
  padding-top: 65px !important;
  padding-right: 6px !important;
  padding-left: 6px !important;
}
#new_post a[class="new_post_label"] {
  font-variant: small-caps;
  text-transform: lowercase;
  font-weight: bold !important;
  font-family: "tahoma" !important;
  font-size: 10px !important;
  white-space: nowrap;
}
#new_post a[class="new_post_label"] {
  color: rgba(0,0,0,0) !important;
}
#new_post a[class="new_post_label"]:before {
  position: absolute;
  color: black;
  width: 50px;
  text-align: center;
  text-indent: -20px;
}

/*ICON TEXT*/
#new_post #new_post_label_text:before {content: "/*[[text-image-name]]*/" !important;}
#new_post #new_post_label_photo:before {content: "/*[[photo-image-name]]*/" !important;}
#new_post #new_post_label_quote:before {content: "/*[[quote-image-name]]*/" !important;}
#new_post #new_post_label_link:before {content: "/*[[link-image-name]]*/" !important;}
#new_post #new_post_label_chat:before {content: "/*[[chat-image-name]]*/" !important;}
#new_post #new_post_label_audio:before {content: "/*[[audio-image-name]]*/" !important;}
#new_post #new_post_label_video:before {content: "/*[[video-image-name]]*/" !important;}

/*ICON IMAGES*/
