@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
domain("hattrick.ws"),
domain("hattrick.interia.pl"),
domain("hattrick.uol.com.br"),
domain("hattrick.org")
{


	/*div.p0 div.shirt,*/ 		/*portiere*/
	div.p1 div.shirt, 			/*ter dx*/
	div.p2 div.shirt,			/*dc dx*/
	div.p3 div.shirt,			/*dc centrale*/
	div.p4 div.shirt,			/*dc sx*/
	div.p5 div.shirt,			/*ter sx*/
	div.p6 div.shirt,			/*ala dx*/
	div.p7 div.shirt,			/*cc dx*/
	div.p8 div.shirt,			/*cc centrale*/
	div.p9 div.shirt,			/*cc sx*/
	div.p10 div.shirt,			/*ala sx*/
	div.p11 div.shirt,			/*att dx*/
	div.p12 div.shirt,			/*att centrale*/
	div.p13 div.shirt,			/*att sx*/
	/* div.p14 div.shirt,*/ 	/*panchina portiere*/
	div.p15 div.shirt,			/*panchina dc*/
	div.p16 div.shirt,			/*panchina cc*/
	div.p17 div.shirt,			/*panchina att*/
	div.p18 div.shirt,			/*panchina ala*/
	div.p19 div.shirt,			/*panchina capitano*/
	div.p20 div.shirt 			/*panchina tiratore*/
	{    
		background-image: url("/*[[myShirt]]*/")!important;
	}
	
	/*div.p0 div.shirt,*/ 		/*portiere*/
	div.p1 div.shirt 	span.nr, 			/*ter dx*/
	div.p2 div.shirt 	span.nr,			/*dc dx*/
	div.p3 div.shirt 	span.nr,			/*dc centrale*/
	div.p4 div.shirt 	span.nr,			/*dc sx*/
	div.p5 div.shirt 	span.nr,			/*ter sx*/
	div.p6 div.shirt 	span.nr,			/*ala dx*/
	div.p7 div.shirt 	span.nr,			/*cc dx*/
	div.p8 div.shirt 	span.nr,			/*cc centrale*/
	div.p9 div.shirt 	span.nr,			/*cc sx*/
	div.p10 div.shirt 	span.nr,			/*ala sx*/
	div.p11 div.shirt 	span.nr,			/*att dx*/
	div.p12 div.shirt 	span.nr,			/*att centrale*/
	div.p13 div.shirt 	span.nr,			/*att sx*/
	/* div.p14 div.shirt 	span.nr,*/ 	/*panchina portiere*/
	div.p15 div.shirt 	span.nr,			/*panchina dc*/
	div.p16 div.shirt 	span.nr,			/*panchina cc*/
	div.p17 div.shirt 	span.nr,			/*panchina att*/
	div.p18 div.shirt 	span.nr,			/*panchina ala*/
	div.p19 div.shirt 	span.nr,			/*panchina capitano*/
	div.p20 div.shirt 	span.nr 			/*panchina tiratore*/
	{    

		color:/*[[myShirtColor]]*/!important;
		line-height:48px!important;
	}

	div.p0 div.shirt, 		/*portiere*/
	div.p14 div.shirt 	/*panchina portiere*/
	{
		background-image: url("/*[[myShirtKeep]]*/")!important;
	}

	div.p0 div.shirt 	span.nr, 		/*portiere*/
	div.p14 div.shirt 	span.nr/*panchina portiere*/
	{
		color:/*[[myShirtKeepColor]]*/!important;
		line-height:48px!important;
	}


}
