css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kingdomofloathing.com"), domain("loathing2.com") {

/* --headers-- */
.header, #recipeHeader,
[bgcolor="blue"], [bgcolor="blue"][width],
[style="background:blue"], [style*="background-color: blue"],
[style*="background-color:blue"],
div.title, th,
/* Familiar */
center> p> table[width="90%"] td[colspan="2"][align="center"],
/* Last Adventure */
center + script + center b> a.nounder[target="mainpane"],
#nudgeblock>font[size="2"] b, /* Current Quest(nudge) */
center> p> b> font[size="2"], /* Effects */
br + center + center > p,
#recipeContent, 
#monname:hover, 
#jumptobot, #InputForm
{  /*[[huey]]*/
}

/* --create borders-- */
center:first-child + center > table:not([width]):first-of-type, /*(check)*/
center:nth-of-type(4) table:not([id]), /*(check)*/
center:nth-of-type(5) table:not([id]), /*(check)*/
table + table[align="center"],
#nudgeblock, 
.item, #current, #testform,
center > div + form + a:hover
{  border: 1px solid;
}

/* --position headers-- */
#nudgeblock, 
center> p> table[width="90%"]
{  color: white;
   margin: -12px -1px 0 -1px; 
   text-align: center;
   border-radius: 5pt;
}

/* --padding-- */
#nudgeblock font a,
center p > b > font[size="2"],
center + script + center a,
br + center + center > p,
font[size="2"] > a[href="questlog.php"] > b,
table + table[align="center"],
center > div + form + a:hover
{  padding: 0px 4px;
}

/* --round corners-- */
center > p > table[width="90%"] td,
#nudgeblock,
center > p > b > font[size="2"],
center + center table,
br + center + center > p,
.i, .item, #body,
input[class*="button"],
center > div + form + a:hover,
#monname,
#current, #testform
{  border-radius: 3pt;
}
/* -top left corners- */
#recipeHeader, /* (for GM scripts)  */
table[width="95%"][cellspacing="0"][cellpadding="1"][style], 
#pop_ircm, #pop_ircm > div,
[bgcolor="blue"]:not([width]),
[bgcolor="blue"][width]:first-child 
{  border-top-left-radius: 9pt;}
td[bgcolor="blue"][width] +td, 
td[bgcolor="blue"][width]:last-child 
{border-top-left-radius: 0 !important;}
/* -top right corners- */
#recipeHeader, /* (for GM scripts) */
table[width="95%"][cellspacing="0"][cellpadding="1"][style],
[bgcolor="blue"]:not([width]),
[bgcolor="blue"][width]:last-child 
{  border-top-right-radius: 9pt;
}
td[bgcolor="blue"][width] +td, 
td[bgcolor="blue"][width]:first-child 
{  border-top-right-radius: 0 !important;
}
/* -bottom corners- */
center + script + center a, #InputForm,
font[size="2"] > a[href="questlog.php"] > b 
{  border-bottom-left-radius: 6pt;
   border-bottom-right-radius: 6pt;
}
/* --misc. overrides-- */

p:empty {display: none !important;}
.item,
table + table[align="center"] {margin-top: -1px !important;}
center > div + form + a {position: relative; top: -8px !important;}
center > div + form + a:hover {text-decoration: none !important;}
#tabs li.active {border-right-color: white !important;}

/* text styles */
#monname {font-weight: 600; padding: 1pt 2pt;}
#rollover,
#rollover + center > table[align="center"] + center {
  font-style: italic; font-size: 10pt; color: inherit;
}

/*END*/
}
