@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/*Australis Transparency/Aero/Glass - Gradient*/



/* Remove Border at the top & bottom of the Navigationbar*/
#nav-bar{
	border-top: 0 !important; box-shadow: none  !important;
}
#navigator-toolbox::after{
	background-color: rgba(0,0,0,.30) !important;
}



/* Square top corners of the Navigation Bar*/	
#nav-bar{
	border-radius: 0px !important;
}



/* Look of the active tab */
@-moz-document url-prefix('chrome://'){
	.tabbrowser-tab .tab-stack{
		height: 29px !important;
		max-height: 29px !important;
		margin: 0 !important;
	}
	.tabbrowser-tab:not([selected]) .tab-stack{
		padding: 1px 4px 0px !important;
	}
	.tabbrowser-tab[selected="true"] .tab-stack{
		padding: 1px 2px 0px !important;
		-moz-border-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAAdCAYAAAB8I5agAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAadEVYdFNvZnR3YXJlAFBhaW50Lk5FVCB2My41LjEwMPRyoQAAATdJREFUOE%2BNzCFLQ2EYhuGzlaGHsTHGGGNjbGOMjcl2yv6CA8V%2FYPC%2FWCwWi8FisVgsFovFYrBYLBaLxWKxWPR54BYO8h7xwM35vve7eJNyuVxRadDmr7dK4kGpVFqvVqsD%2Fbdp5%2BfMfG2XaLiRZdn%2B1x%2Bfl9kZp77IfhYFTvP4Qzgsj6u67Aq%2BF%2BV3VfXmGvhNOAxcM66DX4XDwHXjBvhFOAzcMG6Cn4XDwE3jFvhJOAzcyuNH4bA8brP5QTgM3PbmDvheOAzcMe6C74TDwF3jHvhWOAzcM%2B6Db4TDwH3jAfhaOAw8MB6Cr4TDwEPjEfhSOAw8Mh6DL4TDwGPjCfhcOAw8MZ6Cz4TDwFPjGfhUOAw8M56DT4TDwHPjLbUneFyU3%2B2MF%2BAj4TDwwngJPhQOAy8TgX%2F3DcvG5iYvM0n%2FAAAAAElFTkSuQmCC
		) 0 3 0 3 / 0 3px 0 3px !important;
	}
	#tabbrowser-tabs .tab-content{
		margin: 0 !important;
	}
}



/* Colors+Transparency */

/*  Adressbar */
#urlbar {
	background:-moz-linear-gradient(hsla(0, 0%, 100%, .5), hsla(0, 0%, 100%, 0.1)) !important;
	border: 1px solid rgba(0,0,0,0.5) !important;
	text-shadow:white 0 0 6px, white 0 0 6px, rgba(255, 255, 255, .5) 0 1px 0 !important;
}
#urlbar[focused] {
	background:-moz-linear-gradient(hsla(0, 0%, 100%, .8), hsla(0, 0%, 100%, 0.4)) !important;
	border: 1px solid rgba(31,175,230,1) !important;  /* remove this line if you don't like the blue border */
	text-shadow: none !important;
}
#urlbar:hover {
	background:-moz-linear-gradient(hsla(0, 0%, 100%, .8), hsla(0, 0%, 100%, 0.4)) !important;
	text-shadow: none !important;
}
/*  Navigationbar */
#main-window[tabsontop="true"]:not([#browser-bottombox]) #navigator-toolbox > * {
	background: none !important;
}
#main-window[tabsontop="true"] #nav-bar{
	border: none !important;
	background: -moz-linear-gradient(hsla(0, 0%, 100%, .5), hsla(0, 0%, 100%, 0)) padding-box !important;
}
#main-window[tabsontop="true"] .tabbrowser-tabs {
	margin-bottom:1px!important
}
/*  Findbar */
#browser-bottombox{
	background: -moz-linear-gradient(hsla(0, 0%, 100%, .5), hsla(0, 0%, 100%, 0)) padding-box !important;
}
#browser-bottombox > *{
	background: none !important;
}
#browser-bottombox > #FindToolbar{
	border-top: 1px solid rgba(10%, 10%, 10%, .4) !important;
}



/* Text Colors+Shadows */

/*  Navigationbar/Bookmarkbar */
#main-window deck {
	color: black !important; 
	text-shadow:white 0 0 6px, white 0 0 6px, rgba(255, 255, 255, .4) 0 1px 0 !important;
}
/*  non-active tab hovered */
#main-window[tabsontop="true"] .tabbrowser-tab:not([selected]) .tab-text{
	color: black !important; 
	text-shadow:white 0 0 6px, white 0 0 6px, rgba(255, 255, 255, .5) 0 1px 0 !important;
}
/*  non-active tab */
#main-window[tabsontop="true"] .tabbrowser-tab:not([selected]):not(:hover) .tab-text{
	color: black !important; 
    text-shadow:white 0 0 9px, white 0 0 9px, rgba(255, 255, 255, .4) 0 1px 0 !important; 
}
/*  active tab */
#main-window[tabsontop="true"] tab{
	color: black !important; 
	text-shadow:rgba(255, 255, 255, .4) 0 1px 0 !important;
}



