/* Custom AppChan - For use with "Tomorrow" color scheme option in AppChan
Make sure to always enable AFTER AppChan to override the settings.
Everything is labelled for you here. Play around with settings, use a site like palettebuilder.com to find a nice color scheme from one color.
Do not delete selections here. This will not remove what you want, but rather make it uncustomizable from here. Add "display: none !important;" to the section instead to remove things. 

To uncomment something remove the slashes and stars.
*/

@-moz-document domain("4chan.org") {

/* Custom: */
body {
background-image: none !important;
}

/* Background color. */

	html,
	body {
		background-color: /*[[bgcolor]]*/ !important;
	}

/* Uncomment the following and remove text in () to add in custom background image. Nulls background color choice from above. */
/* Though `body` would suffice, `html > body` ensures this CSS overrides the body declaration used in vanilla AppChan. */
/*	html > body { 
		background-image url('http://upload.com/yourlinkhere.png') !important;
		background-repeat: no-repeat !important;	(Change to repeat, repeat-x, or repeat-y if you do want image to repeat)
		background-attachment: fixed !important;	(Forces the background to remain on the screen while scrolling) 
	} */

/* Text color */

	body, body>form, div[class="navLinks"] a , .postForm, select, .deleteform, .abbr, .boxbar, .boxcontent, .fileInfo, .boardBanner, .pages strong, .reply, .reply.highlight, #imgControls label::after, #qp, #twi_table_div, #updater #count:not(.new)::after, .pages {
		color: /*[[textcolor]]*/ !important;
	}

/* Poster name color */

	.name {
		color: /*[[postername]]*/ !important;
	}

/* Tripcode color */

	.postertrip {
		color: /*[[postertrip]]*/ !important;
	}

/* Quickreply labels text color */

	#qr input:-moz-placeholder, /* Firefox */
	#qr textarea:-moz-placeholder {
		color: /*[[label]]*/ !important;
	}

	#qr input::-webkit-input-placeholder, /* Google Chrome */
	#qr textarea::-webkit-input-placeholder {
		color: /*[[label]]*/ !important;
	}

	#qr input:placeholder, /* Lolopera */
	#qr textarea:placeholder {
		color: /*[[label]]*/ !important;
	}

/* Subject text color */

	.subject, #qp .subject {
		color: /*[[filetitle]]*/ !important;
	}

/* >greentext color */

	.quote {
		color: /*[[unkfunc]]*/ !important;
	}

/* Text color of inputted text in text boxes */

	input, textarea {
		color: /*[[inputfont]]*/ !important;
	}

/* Text color of moot's announcements */

	.globalMessage {
		color: /*[[announcement]]*/ !important;
	}

/* Text color of: 40 posts and 7 imaged ommitted. Click reply to view. */

	.summary {
		color: /*[[ommitted]]*/ !important;
	}

/* Google Chrome's file upload button text color */

	input[type='file']::-webkit-file-upload-button {
		color: /*[[label]]*/ !important;
	}

/* Thread hiding links ("Hide" / "[-]) text color and hover color */

	.sideArrows a {
		color: /*[[replyhider]]*/ !important;
	}

	.sideArrows a:hover {
		color: /*[[alinkhover]]*/ !important;
	}

/* Link text color */

	a,
	#dump,
	a.quotelink,
	.sideArrows a,
	.useremail .postertrip,
	.useremail .name,
	a.replylink {
		color: /*[[alink]]*/ !important;
	}

/* Text color when links are hovered */

	a:hover,
	#dump:hover,
	a.quotelink:hover,
	.sideArrows a:hover,
	.useremail .postertrip:hover,
	.useremail .name:hover,
	div.post div.postInfo span.postNum a:hover {
		color: /*[[alinkhover]]*/ !important;
	}

/* Link hover bubbles */

	#boardNavDesktop a:hover,
	.pages input:hover,
	a:hover {
		background: none !important;
	}

/* 4chan X's quote backlinks and forward links */

	a.backlink,
	a.forwardlink {
		color: /*[[backlink]]*/ !important;
	}

/* Background color of replies, border color */

	div.reply {
		background-color: /*[[tdreply]]*/ !important;
		border: 1px solid /*[[replybc]]*/ !important;
	}



/* Font color and background color of text selection (clicking and dragging over text) */

	::-webkit-selection {
		background: /*[[bgselect]]*/;
		color: /*[[selecttext]]*/;
	}

	::-moz-selection {
		background: /*[[bgselect]]*/;
		color: /*[[selecttext]]*/;
	}

	::selection {
		background: /*[[bgselect]]*/;
		color: /*[[selecttext]]*/;
	}

/* Background color of things like thread watcher and delete buttons, and anything that matches that color */

	#boards h3,
	#boardsMenu,
	#ft li,
	#ft ul,
	#navbar,
	#options .dialog,
	#popup,
	#threadwatcher,
	#threadwatcher .postblock,
	#watcher,
	.box-outer,
	.boxbar,
	.deleteform input[value=Delete],
	.top-box,
	.yuimenuitem-selected {
		background-color: /*[[watcherbg]]*/ !important;
	}

/* Background color of text box fields */

	.recaptchatable #recaptcha_response_field,
	input,
	input[type='file']>input[type='button'],
	input[type='submit'],
	select,
	textarea,
	#options input,
	#options textarea,
	#qr label input,
	#thread_filter input,
	#thread_filter textarea,
	.boxcontent input,
	.boxcontent textarea,
	div.postarea>form td:nth-of-type(3)>label input {
		background-color: /*[[inputbg]]*/ !important;
	}

        input[type='submit'],
        input,
        textarea {
                border: 1px solid /*[[inputbc]]*/ !important;
        }

/* Background color of things like 4chan X's quick reply and 4chan X's options, and anything that matches that color */

	div[id^='4chan_ext_iqr'],
	div[id^='4chan_ext_iqr'] *,
	#filter input,
	#filter textarea,
        #options.dialog,
	#qr,
	#quickrep,
	h3,
	.bd,
	.boxcontent>hr,
	#updater input:not([type='checkbox']),
	div[id^=qr],
	#qr a#attach::after,
	table.reply[style^='clear: both'] {
		background-color: /*[[4chanxbg]]*/ !important;
	}

/* Navigation background color */

	#boardNavDesktop {
		background-color: /*[[headerbg]]*/ !important;
       }

/* Background color of text box fields on hover */

	#backwash_tooltip,
	#cc-showpostarea,
	#filter.autohide:hover,
	#filter:not(.autohide),
	#twi_table_div,
	#twi_table_div *,
	#updater:hover,
	#thread_filter.autohide:hover,
	#thread_filter:not(.autohide),
	#popup,
	.chanchimp,
	.chanchimp>div,
	.recaptchatable #recaptcha_response_field:hover,
	.deletebuttons input[type='password']:hover,
	div[id^=ref],
	input:hover,
	input[type='file']>input[type='button']
	input[type='submit']:hover,
	div.reply.highlight,
	textarea:hover {
		background-color: /*[[inputhbg]]*/ !important;
	}

      input[type='submit']:hover,
        input:hover,
        textarea:hover {
                border: 1px solid /*[[inputbch]]*/ !important;
        }

/* Quote Preview background color */

	#qp {
		background-color: /*[[qpbg]]*/ !important;
                border: 1px solid /*[[qpbc]]*/ !important;
       }

/* Background color of fields when inputting text into them */

	.recaptchatable #recaptcha_response_field:focus,
	.deletebuttons input[type='password']:focus,
	input:focus,
	textarea:focus {
		background-color: /*[[inputfbg]]*/ !important;
	}

        input:focus,
        textarea:focus {
                border: 1px solid /*[[inputbcf]]*/ !important;
        }

/* Quote highlighting outline color for 4chan X */

	.qphl {
		outline-color: /*[[qph1bg]]*/ !important;
	}

/* Checkbox background color at normal state */

	input[type=checkbox],
	#options input[type=checkbox],
	.postForm input[type=checkbox],
	#qr label input {
		background: /*[[cbnbg]]*/ !important;
	}

/* Checkbox background color at active state */

	input[type=checkbox]:active,
	#options input[type=checkbox]:active,
	.postForm input[type=checkbox]:active,
	#qr label input:active {
		background: /*[[cbabg]]*/ !important;
	}

/* Checkbox background color when checked */

	input[type=checkbox]:checked,
	#options input[type=checkbox]:checked,
	.postForm input[type=checkbox],:checked,
	#qr label input:checked {
		background: /*[[cbcbg]]*/ !important;
	}

/* Background color of buttons */

	div[class="navLinks"] a,
	input[type='submit'],
	#com_submit,
	input[type='file']>input[type='button'] {
		background-color: /*[[buttonbg]]*/ !important;
	}

/* Background color when buttons are hovered */

	div[class="navLinks"] a:hover,
	#com_submit:hover,
	input[type='submit']:hover,
	input[type='file']>input[type='button']:hover {
		background-color: /*[[buttonhbg]]*/ !important;
	}

/* Inline Quotes */
        td[style='border: 1px dashed;'],
	.inline div.reply {
		background-color: rgba(0,0,0,0.1) !important;
		border: none !important;
	}

/* Pages */

	.pages strong {
		color: /*[[textcolor]]*/ !important;
	}

	.pages {
		background-color: /*[[pagesbg]]*/ !important;
	}

	.new {
		background-color: rgba(255,255,255,.5) !important;
		color: #000 !important;
	}

/* Top of thread watcher's bottom border color */

	#watcher .move {
		border-bottom: 1px /*[[watcherbb]]*/ solid !important;
	}

/* Thread separation lines */

	hr {
		border-top: 1px solid /*[[hrcolor]]*/ !important;
	}

/* Tabbed post form and quick reply background color for tab */

	.postForm::before,
	#qr::before {
		background-color: /*[[tabbed]]*/ !important;
		border-radius: 6px 6px 0 0;
		-o-border-radius: 6px 6px 0 0;
	}

/* Only uncomment the following if you use custom scrollbars and want / know how to customize them */

/*
	::-webkit-scrollbar-thumb:hover { background: #373b41; }
	::-webkit-scrollbar-thumb:active { background: #1d1f21; }
	::-webkit-scrollbar-track-piece, ::-webkit-scrollbar-corner, ::-webkit-scrollbar-resizer { background: #282a2e; }
	::-webkit-scrollbar-thumb { background: #282a2e; border: #373b41 !important; }
	scrollbar, scrollcorner { background: #373b41 !important; }
	thumb { border-color: #373b41 !important; background: #282a2e; }
	thumb:active { background: #1d1f21 !important; }
	thumb:hover { background: #373b41; }
*/

/* Only uncomment the following if you use transparent replies and want to customize the shadow colors */

/*
	div.reply {
		background-color: rgba(40,42,46,0.5) !important; /* Number order: (Red, Green, Blue, Alpha/Opacity) */
	}
	div.reply.highlight {
		background-color: rgba(55,59,65,0.5) !important;
	}
*/

/*[[popout]]*/
}
