css:
/* By Pullipo - http://pullipo.deviantart.com/ */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* --- Quick modifier --- */
/*[[sharpie]]*/

/* --- Base font --- */
#main-window {
font-family: "Ubuntu" !important}

#appmenu-button .dropmarker-icon {
display:none !important}

/* --- Tab position --- */
#main-window[sizemode="normal"] #TabsToolbar {
margin-left: -4px !important;
margin-right: -4px !important}

#main-window[sizemode="maximized"] #TabsToolbar {
margin-right: -16px !important}

/* --- Dim tab favicon --- */
.tabbrowser-tab:not([selected]) .tab-icon-image {
opacity: .25 !important}

.tabbrowser-tab:hover .tab-icon-image {
opacity: 1 !important}

/* --- Dim tab name --- */
.tabbrowser-tab:not([selected]) {
color: rgba(0,0,0,.5) !important}

.tabbrowser-tab:hover {
color: black !important}

/* --- Dim close button --- */
.tab-close-button {
opacity: 0 !important}

.tabbrowser-tab:hover .tab-close-button {
opacity: 1 !important}

/* --- New tab --- */
.tabs-newtab-button {
background: none !important;
border: none !important;
opacity: .5 !important;
-moz-transition: .15s ease-out !important;
list-style-image: url("chrome://browser/skin/tabbrowser/newtab-inverted.png") !important}

.tabs-newtab-button:hover {
opacity: 1 !important;}

/* --- Navigation bar --- */
#nav-bar {
padding: 2px 3px 2px 3px !important}

#main-window:not([sizemode="maximized"]) #nav-bar {
border-radius: 0 3px 0 0 !important}

/* --- URL bar --- */
.ac-type-icon/*, #star-button*/,
.autocomplete-history-dropmarker,
#feed-button, .search-go-button, .search-go-button-stack,
#go-button, .search-go-button {
display: none !important}
 
#star-button {
padding-right: 3px !important;}

/* --- Bookmark bar --- */
#PersonalToolbar {
text-shadow: 0 1px 0 rgba(255,255,255,.85) !important;
padding: 0 3px 2px 3px !important}

#PersonalToolbar:not([customizing])[collapsed="false"] #bookmarks-menu-button > .toolbarbutton-icon {
height: 18px !important;
width: 18px !important}

/* ----- Bookmark toolbar new hovered state ----- */
toolbarbutton.bookmark-item {
border: transparent 1px solid !important;
padding: 0 4px 0 2px !important;
border-radius: 3px !important;
-moz-appearance: none !important;
-moz-transition: box-shadow .1s ease-in !important}
toolbarbutton.bookmark-item:hover {
box-shadow: 0 20px 20px -22px rgba(0, 0, 0, .5) inset !important}
toolbarbutton.bookmark-item:hover:active, toolbarbutton.bookmark-item[open] {
box-shadow: 0 20px 20px -22px rgba(0, 0, 0, 1) inset !important}

/* --- Panorama --- */
#main-window[sizemode="normal"] #tab-view {
margin-top: -3px !important;
margin-left: -10px !important;}

#main-window[sizemode="maximized"] #tab-view {
margin-top: 5px !important;
margin-left: -2px !important;}
