css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com') {
#right_column > UL:first-child > LI:last-child > A {
  height: 38px !important;
}
#right_column > UL:first-child > LI:last-child > A:before,
#right_column > UL:first-child > LI:last-child > A:after,
#right_column > UL:first-child > LI:last-child > A > DIV:first-child:before,
#right_column > UL:first-child > LI:last-child > A > DIV:first-child:after {
  position: absolute !important;
  content: "" !important;
  display: block !important;
  width: 42px !important;
  height: 25px !important;
  z-index: 1001 !important;
}
#right_column > UL:first-child > LI:last-child > A:before ,
#right_column > UL:first-child > LI:last-child > A:after {
   border-right: double 10px /*[[bordcol]]*/ !important;
}
#right_column > UL:first-child > LI:last-child > A > DIV:first-child:before,
#right_column > UL:first-child > LI:last-child > A > DIV:first-child:after {
   border-left: double 10px /*[[bordcol]]*/ !important;
}
#right_column > UL:first-child > LI:last-child > A:before,
#right_column > UL:first-child > LI:last-child > A > DIV:first-child:before {
   margin-top: 23.75px !important;
}
#right_column > UL:first-child > LI:last-child > A:after,
#right_column > UL:first-child > LI:last-child > A > DIV:first-child:after {
  margin-top: 6.75px !important;
}
#right_column > UL:first-child > LI:last-child > A:before {
  background: /*[[farleft]]*/ !important;
  margin-left: -36px !important;
}
#right_column > UL:first-child > LI:last-child > A:after {
  background: /*[[midleft]]*/ !important;
  top: 25px !important;
  margin-left: 16px !important;
}

#right_column > UL:first-child > LI:last-child > A > DIV:first-child:before {
  background: /*[[midrite]]*/ !important;
  margin-left:65px !important;
}
#right_column > UL:first-child > LI:last-child > A > DIV:first-child:after {
  background: /*[[farrite]]*/ !important;
  top: 24px !important;
  margin-left:117px !important;
  z-index: 1003 !important;
}}
