css:
/* Userstyles with Changed Background
 * Author    : Kudeshido
 */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org")
{
    html, body
        {color: #FFFFFF !important; background: /*[[background]]*/ !important; background-attachment: fixed !important; background-position: center !important; background-repeat: repeat !important;}
    #main-header
        {background: none !important;}
    a
        {color: #FFFFFF !important;}
    #left-sidebar > *
        {background: none !important;}
    #install
        {background: none !important; color: #FFFFFF !important;}
    pre
        {background: none !important;}
    .edit-style-option:nth-child(2n+1)
        {background: none !important;}
    .ad
        {display:/*[[advert]]*/ !important;}
    #vanilla_discussions_index
        {background: none !important;}
    a.BigButton
        {background: none !important;}
    #Head
        {background: none !important;}
    .MessageForm, .Tabs, .Headings
        {background: none !important;}
    .TabLink, .TabLink:link, .TabLink:hover, .TabLink:active, .TabLink:visited
        {background: none !important;}
    .Active .Tablink
        {background: none !important;}
    .DataList .Meta a.Category
        {background: none !important;}
    .Box
        {background: none !important;}
    .PanelInfo li
        {background: none !important;}
    #vanilla_post_discussion
        {background: none !important;}
    #DiscussionForm form
        {background: none !important;}
    #DiscussionForm label
        {color: #FFFFFF !important;}
    #vanilla_discussion_index
        {background: none !important; color: #FFFFFF !important;}
    #dashboard_activity_index
        {background: none !important; color: #FFFFFF !important;}
    .Activities li.Condensed .Excerpt
        {color: #FFFFFF !important;}
    .Activities .DataList li
        {background: none !important;}
    .Foot
        {background: none !important;}
    #conversations_messages_all
        {background: none !important; color: #FFFFFF !important;}
    #dashboard_profile_index
        {background: none !important; color: #FFFFFF !important;}
    .Box dl dt
        {color: #EEEEEE !important;}
    h1, h2, h3, h4, h5, h6
        {color: #EEEEEE !important;}
    span.Title
        {color: #00F0FF !important;}
    #vanilla_categories_index
        {background: none !important; color: #FFFFFF !important;}
    .Tabs .SubTab
        {background: none !important;}
    .Attachment:hover .FileHover
        {background: #000000 !important;}
    code, pre
        {background: none !important;}
    #post-install-ad
        {display: none !important;}
    .MessageList .Item, body.Discussion #Content, .DiscussionsTabs
        {background: none !important;}
    .DataList .Item
        {background: none !important;}
}
