@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/* AGENT_SHEET */

#navigator-toolbox::after {
  background-color: #7A7A7A !important;
}

.bookmark-item > .toolbarbutton-text:not(:-moz-lwtheme) {
  color: #000000 !important;
}

toolbarbutton.bookmark-item[container] {
  list-style-image: url("data:image/png;base64,")!important;
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
}

toolbarbutton.bookmark-item[livemark] {
  list-style-image: url("data:image/png;base64,")!important;
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
}

toolbarbutton.bookmark-item[query] {
  list-style-image: url("data:image/png;base64,")!important;
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
}

#navigator-toolbox[tabsontop="true"] > #nav-bar,
#navigator-toolbox[tabsontop="true"]:not([customizing]) > #nav-bar[collapsed="true"] + toolbar,
#navigator-toolbox[tabsontop="true"]:not([customizing]) > #nav-bar[collapsed="true"] + #customToolbars + #PersonalToolbar {
  background-image: -moz-linear-gradient(#E6E5E5, #DADAD9, #CFCECF) !important;
}

#navigator-toolbox[tabsontop="true"]:-moz-lwtheme > #nav-bar,
#navigator-toolbox[tabsontop="true"]:not([customizing]):-moz-lwtheme > #nav-bar[collapsed="true"] + toolbar,
#navigator-toolbox[tabsontop="true"]:not([customizing]):-moz-lwtheme > #nav-bar[collapsed="true"] + #customToolbars + #PersonalToolbar {
  background-image: -moz-linear-gradient(rgba(230,229,229,1), rgba(230,229,229,0)) !important;
}

#navigator-toolbox[tabsontop="true"]:not(:-moz-lwtheme) > #PersonalToolbar  {
  background-image: -moz-linear-gradient(#CECECF, #CECECF) !important;
}

#main-window[tabsontop=true][disablechrome] #navigator-toolbox::after {
  background-image: -moz-linear-gradient(#F9F9F9, #E6E5E5, #E6E5E5) !important;
}

#addons-page #header {
  background-image: -moz-linear-gradient(#E6E5E5, #DADAD9, #CFCECF)!important;
}

.statuspanel-label {
  background: #EBEBEB!important;
  color: #000000!important;
}

