css:
/* AGENT_SHEET */
/* ::::::::::::::::::::::::::::::::::::: about:blank, about:config, error & image only pages ::::::::::::::::::::::::::::::::::::::::::: */										
@-moz-document url-prefix(chrome:), url-prefix(about:){

*{      font-family: Tahoma, "Trebuchet MS", Ubuntu, Helvetica; }
.viewContainer {    font-size: 10pt !important;     }

/* Customize the notification bar color: */
notification{   margin: -1px; color: rgba(255,255,255,.9) !important;
                text-shadow: rgba(0, 0, 0, 1) 0px -1px !important;
                background: linear-gradient(#422, #900) !important; }
notification button{ margin-top: 2px; }}
        
@-moz-document url(chrome://browser/content/browser.xul){
tabpanels{	background: #3E3E3E   /* TODO: Change here */ url("/*[[bg]]*/") center center fixed !important; box-shadow: none !important; border-radius: 0 !important;}

/* Full Screen Hover Bars on Top */
#navigator-toolbox[inFullscreen="true"]:hover {transition: none !important;}
#navigator-toolbox[inFullscreen="true"] {transition: transform .4s 0s ease-out; margin:0 0 -75px!important; height:75px!important; opacity:.95 !important;}
#main-window #navigator-toolbox[inFullscreen="true"] #TabsToolbar {background-color: rgba(0,0,0,.8) !important;}
#main-window[inFullscreen="true"] #fullscr-toggler[collapsed="true"] {visibility: hidden !important;}
#navigator-toolbox[style*="margin"][inFullscreen="true"] {transform:translateY(-75px)!important;}

}

@-moz-document url-prefix(about:) {

html, tabbrowser tabpanels, tabpanels, body, #newtab-scrollbox,
page{	background: #3E3E3E /* TODO: And here */ url("/*[[bg]]*/")  center center fixed !important;
        color:  #DDD !important;}
        
*{		border-color: transparent !important;	}

#continue-button{ -moz-appearance: button !important; }

#config{	   background: linear-gradient(#2D2D2D, #595959) !important;	}
#configTree{   margin: 5px -1px -1px  0 !important;	}
#filterRow{    color: rgba(255,255,255,.8) !important;
			   text-shadow: rgba(0, 0, 0, 1) 0px -1px !important; }

#configTreeBody{    background: #444 !important; 
                    color: #ccc !important;}

#errorPageContainer, #launcher, .newtab-cell, #contentContainer, #restorePreviousSession, #searchText,
.launchButton:hover, #addon-container{	background-color: rgba(0,0,0,.4) !important;
			     	                    color: rgba(255, 255, 255, .8) !important;
                    		          	box-shadow: inset #000 0 3px 3px !important;		}
                    		          	
#contentContainer{	background: linear-gradient(rgba(0,0,0,.9), transparent) !important; }             		          	
                    		          	
.newtab-title{ background: linear-gradient(transparent, rgba(0,0,0,.9)) !important; color: #FFF !important;
               text-shadow: black 0 0 1px, black 0 0 2px, black 0 0 3px !important; }    
               
#addon-container{	background-image: none !important;	}

#errorTitleText,#errorShortDescText{	border-bottom: 0 !important;	}

#moreInfoLink, a{		color: steelblue !important;	}
#moreInfoLink:hover{	color: tomato !important;		}}

/*:: GLOBAL */ @-moz-document url(chrome://browser/content/browser.xul), url-prefix(chrome:), url-prefix(about:){ /* BEGINS ::*/

/* ::::::::::::::::::::::::::::::::::::: Hide \ Remove Various Things ::::::::::::::::::::::::::::::::::::::::::: 
Context Menu  */
#context-keywordfield, #context-searchselect, #context-sep-bidi, #context-bidi-page-direction-toggle, #context-bidi-text-direction-toggle
        {  display: none !important; }

/*  Open All in Tabs  */
.bookmarks-actions-menuseparator,
menuitem[label="Open All in Tabs"]{ display: none !important;   } 

/* ::::::::::::::::::::::::::::::::::::::::: Downloads Panel ::::::::::::::::::::::::::: */

#downloadsListBox{ background: linear-gradient(#eee, #333 30%) !important; }
#downloadsFooter{ background: linear-gradient(#333, #111) !important; }

#downloadsPanel:not([keyfocus]) > #downloadsListBox > richlistitem[type="download"][state="1"]:hover {
        background: rgba(0,0,0,.6) !important;
        background-image: -moz-radial-gradient(50% 0%, RGBa(/*[[dc]]*/, .8) -10%, transparent 50%) !important;
        border: none !important;     border-radius: 0 0 6px 6px !important;
        border-top: 2px solid RGBa(/*[[dc]]*/ , .8) !important;
        font-weight: bold !important;   }

/* ::::::::::::::::::::::::::::::::::::::::::::::: Bookmark Bar ::::::::::::::::::::::::::::::::::::::::::: */
#navigator-toolbox #PersonalToolbar{    -moz-appearance: none !important;
                                         background: linear-gradient(#111, transparent) !important;    }
.bookmark-item{    	 color: #eee !important;
			         text-shadow:  #000 0 0 1px, #000 0 0 1px, #000 0 0 2px, #000 0 0 3px !important;}

.bookmark-item:hover{   -moz-appearance: none !important;
                        background: -moz-radial-gradient(50% 0%, RGBa(/*[[bc]]*/, .8), transparent 70%) !important; 
                      /*  box-shadow: inset #000 0 2px 2px !important; */
                        border: none !important;
                        border-top: 1px solid RGBa(/*[[bc]]*/, .8) !important;
                        font-weight: bold !important;  
                        color: #fff !important;  }
 
#main-window[privatebrowsingmode] 
.bookmark-item:hover{   -moz-appearance: none !important;
                        background: -moz-radial-gradient(50% 0%, RGBa(/*[[pbc]]*/, .8), transparent 70%) !important; 
                        border-top: 1px solid RGBa(/*[[pbc]]*/, .8) !important;  }

/*[[AutoBookmark]]*/

/* :::::::::::::::::::::::::::::::::::::::::: Print Preview Toolbar ::::::::::::::::::::::::::::::::::::::::::::::::::::*/

#print-preview-toolbar{ -moz-appearance: none !important;    
                        background: url("chrome://global/skin/media/imagedoc-lightnoise.png"), linear-gradient(#2d2d2d, #595959);  }

#print-preview-toolbar,                        
#print-preview-toolbar [label="Portrait"], 
#print-preview-toolbar [label="Landscape"] { color: #ccc !important;
                                            text-shadow: #080808 0px -1px;	} 

/* ::::::::::::::::::::::::::::::::::::::::::::::: URL bar, Search Bar & WebSearchPro ::::::::::::::::::::::::::::::::::::::::::: */
/* Background of URL bar etc. */
#nav-bar{   border: none !important;
            height: 28px !important;   }

#main-window[sizemode="fullscreen"] #nav-bar{  margin: 0 !important;   height: 29px !important;  } 
                                                            
#navigator-toolbox #toolbar-menubar{    background: 0 !important; }
#navigator-toolbox > *{  background: 	url("chrome://global/skin/media/imagedoc-lightnoise.png"), linear-gradient(#2d2d2d, #595959) !important; 
                         } /* Wants a specific value */

#main-window:not([disablechrome="true"]) #navigator-toolbox:after, #TabsToolbar
	{background: transparent !important; }

/*favicon background */
#identity-box{    padding: 0 !important; box-shadow: none !important;
                  background:  none !important;
                  margin-left: -3px !important; 
                  opacity: .7 !important;       }
                  
#identity-box.verifiedIdentity{ color: rgb(185, 255, 100) !important; }
#identity-box.verifiedDomain{   color: rgb(146, 215, 252) !important; }

/* URL Bar Size */
#urlbar{ height: 24px !important;   }
/* Search Bar Size */
#search-container .searchbar-textbox {  padding: 1px !important;    }

/*Web Search Pro  */  
#MMSearchTBTextbox{ margin-right: 2px !important; margin-left: -2px !important; }

#MMSearchTBTextbox stack {   background-image: linear-gradient(rgba(0,0,0, .1) 15%, rgba(220,220,220, .5))!important;
                             margin-left: -2px !important;       
                             border-radius:4px !important;  }

#MMSearchTBTextbox, .textbox-input-box:not([empty="true"]) {    font-weight:100;
                                                                font-size: 13px !important;
                                                                color: rgba(255,255,255,.7) !important;
                                                                text-shadow: rgba(0,0,0,.6) 0px -1px !important;      }

.autocomplete-textbox-container{ border-radius: 5px !important; padding: 0 2px !important; }

#feed-button, #star-button, #go-button, #flagfox-addressbarpanel, #flagfox-icon
{ opacity: .6 !important; }
#feed-button:hover, #star-button:hover, #go-button:hover, #flagfox-addressbarpanel:hover
{ opacity: 1 !important; }


/* ::::::::::::::::::::::::::::::::::::: Custom Firefox Menu Button ::::::::::::::::::::::::::::::::::::::::::: */

#appmenu-button{	background:  linear-gradient(transparent, rgba(200, 200, 200, .3) 99%) !important;
                    margin-top: -1px !important;
                    box-shadow: inset rgba(200, 200, 200, .3)1px -1px 1px, inset rgba(200, 200, 200, .3) -1px -1px 1px  !important;
                    border: 1px solid rgba(50,50,50,.6) !important;
                    border-radius: 0 0 5px 5px !important;}
                    					
#main-window[sizemode="normal"] #appmenu-button{      max-height: 21px !important; margin-left: 5px !important;   }
                    					
#appmenu-button .button-text{   color: rgba(255, 255, 255, .8) !important;
                                text-shadow: #000 0 -1px !important; }    

#appmenu-button:hover .button-text{   color: white !important;
                                      text-shadow: #600 0 -1px !important; }                  					
                                                                  
#appmenu-button:hover,
#main-window[sizemode="maximized"]
#appmenu-button:hover  {	background: linear-gradient(rgb(/*[[lc]]*/), rgb(/*[[dc]]*/)) !important;
                            box-shadow: inset #fff 0 0 4px !important; }

#main-window[privatebrowsingmode]
#appmenu-button:hover { background: linear-gradient(rgb(/*[[plc]]*/), rgb(/*[[pdc]]*/)) !important; }

#main-window[sizemode="maximized"] 
#appmenu-button{	border: none !important;
			background: transparent;
			box-shadow: inset rgba(20, 20, 20, .7) 2px 2px 6px !important;
    			min-width: 1px !important;
			width: 40px !important;	}
					
#main-window[sizemode="maximized"]
#appmenu-button .button-text{	display: none;	}

#main-window[privatebrowsingmode][sizemode="maximized"] 				
#appmenu-button .button-icon{	 list-style-image: url("chrome://browser/skin/privatebrowsing-light.png") !important; }

#main-window[sizemode="maximized"]
#appmenu-button .button-icon{	margin-left: 2px !important;
								list-style-image: url("chrome://branding/content/icon16.png") !important;
								opacity: .85;	}

#main-window[sizemode="maximized"] 
#appmenu-button > .button-box > .button-menu-dropmarker{	background: transparent !important;
															margin: 0 0 0 5px !important;
															opacity: .5;}		
#main-window[privatebrowsingmode][sizemode="maximized"] 
#appmenu-button > .button-box > .button-menu-dropmarker{	margin: 0 0 0 2px !important;  }	
#appmenu-button:hover .button-icon,
#appmenu-button:hover > .button-box > .button-menu-dropmarker{	opacity: 1 !important;	}
															
/*::::::::::::::::::::::::::::::::Tabs::::::::::::::::::::::::
   ---                          All Tabs --- */

.tabbrowser-tab{	-moz-appearance: none !important;
					font-size: 10pt !important;
					text-align: center !important;
					border: none !important;
					background: transparent !important;
					border-radius: 5px 5px 0 0 !important;
					margin: 0 1px !important;}
		
.tabbrowser-tab .tab-text{ transition: color .2s ease 0s, text-shadow .5s ease .2s; }
					
#main-window[sizemode="normal"] .tabbrowser-tab{	padding:  4px 0 !important;   margin: 1px !important;   }
#main-window[sizemode="normal"] .tabbrowser-tabs .progress-bar{     margin-top: -2px !important; }				

#main-window[sizemode="maximized"] .tabbrowser-tab .tab-text,
#main-window[sizemode="maximized"] .tab-close-button,
#main-window[sizemode="maximized"] .tab-icon-image, 
#main-window[sizemode="maximized"] .tab-throbber,
#main-window[sizemode="maximized"] .tab-throbber[progress]{     margin-bottom: -2px !important;  }

/* --- Selected Tabs --- */

.tabbrowser-tab[selected="true"]{    background: 	url("chrome://global/skin/media/imagedoc-lightnoise.png"),  linear-gradient(#595959, #2D2D2D 60%) !important; }

.tabbrowser-tab[selected="true"] .tab-text{	color: RGBa(/*[[lc]]*/, .9) !important;
											text-shadow: RGBa(/*[[bc]]*/ , .95) 0px 2px 5px,  RGBa(/*[[bc]]*/, .6) 0px -2px 6px, #333 0px -1px;
											font-size: 10.5pt !important; font-weight: bold !important;   }

#main-window[privatebrowsingmode]
.tabbrowser-tab[selected="true"] .tab-text{ color: RGBa(/*[[plc]]*/, .9) !important;
                                            text-shadow: RGBa(/*[[pbc]]*/, .95) 0px 2px 5px,  RGBa(/*[[pbc]]*/, .6) 0px -2px 6px, #333 0px -1px !important; }

/* --- Unselected Tabs  & New Tab Button--- */

.tabbrowser-tab:not([selected="true"]){        background: 	url("chrome://global/skin/media/imagedoc-lightnoise.png"), linear-gradient(RGBa(40,40,40,.5), RGBa(0,0,0,.85) 99%) !important; }

.tabbrowser-tab:not([selected="true"]) .tab-text{   color: #AAA !important;
                                                    text-shadow: #080808 0px -1px !important;	}

.tabbrowser-tab:not([selected="true"]) .tab-icon-image{ opacity: .8 !important; }
													
.tabs-newtab-button{    background:  radial-gradient(28px 28px, RGBa(255,255,255, .9), transparent 30%) !important;
                        border: none !important;
                        padding: 0 11px !important;    }

.tabs-newtab-button:hover{  background: radial-gradient(21px 21px, RGB(/*[[bc]]*/ ), transparent 60%) !important;	}

#main-window[privatebrowsingmode]
.tabs-newtab-button:hover{  background: radial-gradient(21px 21px, RGB(/*[[pbc]]*/), transparent 60%) !important;	}

/* --- Progress Stuff & favicons --- */

.tabbrowser-tab:not([selected="true"]) .progress-bar{		opacity: .4 !important; }
.tabbrowser-tab:not([selected="true"]) .tab-throbber[progress]{	opacity: .5 !important; }

.tabbrowser-tabs .progress-bar{	max-height: 3px !important;
								margin-left: 4px !important;
								background: linear-gradient(RGB(/*[[lc]]*/ ), RGB(/*[[bc]]*/)) !important;
								box-shadow: 1px 0 2px RGBa(/*[[dc]]*/, .95), -1px 0 2px RGBa(/*[[dc]]*/, .95),  0px -1px 2px RGBa(/*[[lc]]*/, .7) !important;  
								border-radius: 5px !important;  }

.tab-throbber[progress]{	list-style-image: url("data:image/png;base64,/*[[throbber]]*/") !important;	}

#main-window[privatebrowsingmode]
.tabbrowser-tabs .progress-bar{	background: linear-gradient(RGB(/*[[plc]]*/), RGB(/*[[pbc]]*/)) !important;
				box-shadow: 1px 0 2px RGBa(/*[[pdc]]*/, .95), -1px 0 2px RGBa(/*[[pdc]]*/, .95),  0px -1px 2px RGBa(/*[[plc]]*/, .7) !important;  }

#main-window[privatebrowsingmode]
.tab-throbber[progress]{	list-style-image: url("data:image/png;base64,/*[[pthrobber]]*/") !important;	}

.tab-icon-image, 
.tab-throbber,
.tab-throbber[progress]
{ margin-left: 4px !important; }

/*::::::::::::::::::::::::: Bigger App Tabs ::::::::::::::::::::::::::::::*/

.tabbrowser-tab[pinned]{	min-width: 50px !important;
                            max-width: 50px !important;	} 

.tabbrowser-tab[pinned] .tab-text{	display: none !important;	}

.tabbrowser-tab[pinned] .tab-icon-image, .tabbrowser-tab[pinned] .tab-throbber, .tabbrowser-tab[pinned] .tab-throbber[progress]
    {	margin-left: 17px !important;	}

/* :::::  Find Bar Style :::::  */

#FindToolbar, findbar{	    -moz-appearance: none !important;
                    position: fixed;
                    bottom: 0px;  border: 0 !important; margin: 0 !important; padding: 7px 5px 0 0 !important;
			        background: linear-gradient( transparent 10%, rgbA(0,0,0,.5), #111)  !important; 
			        box-shadow: none !important;
			        transition: opacity .3s ease 0s !important; }

#FindToolbar, #FindToolbar .toolbarbutton-text, #status-bar, findbar, .findbar-highlight,
.findbar-case-sensitive, .findbar-find-status {	font-size: 12px !important;
                                                color: #ddd !important;
                                                text-shadow: #000 0px -1px !important;	
                                                border-radius: 0 15px 0 0 !important;}
                                                                
.findbar-case-sensitive:hover,  .findbar-highlight:hover,
.findbar-case-sensitive:active,  .findbar-highlight:active{  -moz-appearance: none !important;
                        background: -moz-radial-gradient(50% 100%, RGBa(/*[[bc]]*/, .8), transparent 70% ) !important; 
                        border: none !important;
                        border-bottom: 1px solid RGBa(/*[[bc]]*/, .8) !important;
                        font-weight: bold !important;  
                        color: #fff !important; }
                        
#main-window[privatebrowsingmode] .findbar-case-sensitive:hover, #main-window[privatebrowsingmode] .findbar-highlight:hover,
#main-window[privatebrowsingmode] .findbar-case-sensitive:active, #main-window[privatebrowsingmode] .findbar-highlight:active
{                       background: -moz-radial-gradient(50% 100%, RGBa(/*[[pbc]]*/, .8), transparent 70% ) !important; 
                        border-bottom: 1px solid RGB(/*[[pbc]]*/) !important;     }
                        
.findbar-find-status{ font-style: italic; padding-right: 30px !important;}

.findbar-textbox[status="notfound"]{    background: linear-gradient(RGBa(100,0,0, .8), RGBa(200,0,0, .1)) !important;   }
.findbar-textbox[status="notfound"][focused="true"],
#main-window[privatebrowsingmode] 
.findbar-textbox[status="notfound"][focused="true"]{   box-shadow: inset rgba(0,0,0,.6) 0 2px 2px 2px, 
                                                       red 0 0 4px !important;   }

.findbar-textbox{ -moz-margin-start: 5px !important; }

.findbar-find-previous:hover,
.findbar-find-next:hover{   -moz-appearance: none !important;
                            background-color: rgba(/*[[lc]]*/, .8) !important;
                            background-image: linear-gradient(rgba(255, 255, 255, .8), rgba(255, 255, 255, .2)),
                                              linear-gradient(rgba(/*[[lc]]*/, .4), rgba(/*[[lc]]*/, .1)) !important;
                            border-color: rgba(/*[[dc]]*/, .9) rgba(/*[[bc]]*/, .8) rgba(/*[[lc]]*/, .8) !important;
                            box-shadow: 0 0 2px rgba(/*[[lc]]*/,0), 0 0 3.5px 1px rgb(/*[[dc]]*/),
                                        0 1px 0 rgba(255, 255, 255, .4) inset !important;  }

#main-window[privatebrowsingmode] 
.findbar-find-previous:hover,
#main-window[privatebrowsingmode] 
.findbar-find-next:hover{   -moz-appearance: none !important;
                            background-color: rgba(/*[[plc]]*/, .8) !important;
                            background-image: linear-gradient(rgba(255, 255, 255, .8), rgba(255, 255, 255, .2)),
                                              linear-gradient(rgba(/*[[plc]]*/, .4), rgba(/*[[plc]]*/, .1)) !important;
                            border-color: rgba(/*[[pdc]]*/, .9) rgba(/*[[pbc]]*/, .8) rgba(/*[[plc]]*/, .8) !important;
                            box-shadow: 0 0 2px rgba(/*[[plc]]*/,0), 0 0 3.5px 1px rgb(/*[[pdc]]*/),
                                        0 1px 0 rgba(255, 255, 255, .4) inset !important;  }
                                        
.findbar-find-previous:active, 
.findbar-find-next:active,
.findbar-find-previous:hover:active,
.findbar-find-next:hover:active{    background-color: rgba(0, 0, 0, .8) !important;
                                    background-image: linear-gradient(rgba(255, 255, 255, .8), rgba(255, 255, 255, .2)) !important; 
                                    border-color: rgba(0, 0, 0, .25) rgba(0, 0, 0, .27) rgba(0, 0, 0, .32) !important;
                                    box-shadow: inset 0 1px 3px rgba(0, 0, 0, .7) !important;    }

#navigator-toolbox:not([customizing]) ~ #browser-bottombox{     background: none !important;  
                                                                border: none !important;   }

/*[[PM]]*/

/* ::::::::::::::::::::::::::::::::::::::::::::::: hovered URL ::::::::::::::::::::::::::::::::::::::::::: */

statuspanel{					bottom: 0px !important; z-index: 999;	}

.statuspanel-label{	background: RGBa(0,0,0,.7) !important;
					color: #ddd !important;
					text-shadow: #000 0px -1px !important;		
					border: 0 !important; border-radius: 5px 5px 0 0 !important;   
					padding: 0 8px !important; margin: 0 8px !important;
					box-shadow: inset RGBa(0,0,0,.7) 0 1px 3px !important;
}  

#appcontent > * {      -moz-box-ordinal-group: 3;    }

tabbrowser {      -moz-box-ordinal-group: 2 !important;    }

statuspanel-inner {      -moz-box-align: start !important;    }

statuspanel-label{	border-top-left-radius: 0 !important;
					border-top-right-radius: 0 !important;	}

.statuspanel-label:-moz-locale-dir(ltr):not([mirror]), 
.statuspanel-label:-moz-locale-dir(rtl)[mirror]{	border-bottom-right-radius: .3em;
					border-right-style: solid;	}


/* :::::: Add-On Bar ::::: */

#navigator-toolbox:not([customizing]) ~ #browser-bottombox{     background: none !important;  
                                                                			border: none !important;   }
                                                                
/* :::::::::::::: Sidebar :::::::::::::: */

sidebarheader,
#sidebar-title,
.sidebar-placesTree,
#history-panel > hbox, 
#bookmarksPanel > hbox{	-moz-appearance: none !important;
						color: rgba(255, 255, 255, .8) !important;
						text-shadow: #000 0px -1px !important;        }

sidebarheader{	background: linear-gradient(#595959, #2D2D2D) !important;
				border: none !important;
				padding-right: 10px !important;
				margin-right: -10px !important;		}

#sidebar-title{	font-weight: bold  !important; font-size: 13px !important;
				        text-align: center !important;	}

.sidebar-placesTree,
#sidebar-splitter{	background: linear-gradient( #2D2D2D, #595959) !important;
					border: none !important;		}


#sidebar-splitter{	margin: 0 -1px !important;    }


#history-panel > hbox, #bookmarksPanel > hbox{	background: #2D2D2D !important;        }

/*:::::::::::::::::::::::::::::::::::::::::::::::::::: Library ::::::::::::::::::::::::::::::::::::::::::::::::::::*/

@-moz-document url("chrome://browser/content/places/places.xul") {

#placesToolbar{	-moz-appearance: none !important;
				background: linear-gradient(#2D2D2D, #595959) !important;
				text-shadow: #000 0px -1px !important;	
				padding: 4px 5px 4px 5px !important;
				border: none !important;				}

#placesToolbar *{	color: rgba(255,255,255,.8) !important;	}

.toolbarbutton-1[disabled="true"] .toolbarbutton-icon{	opacity: .2 !important;	}


tree[type="places"],
#contentView,
#contentView .tree-bodybox{	background: linear-gradient(#2D2D2D, #595959) !important;
                            color: rgba(255,255,255,.8) !important;
                            text-shadow: rgba(0, 0, 0, 1) 0px -1px !important;
                            border: none !important;				}}

/*:::::::::::::::::::::::::::::::::: Darken All Text Boxes :::::::::::::::::::*/

textbox{    -moz-appearance: none !important;
            background: linear-gradient(rgba(10,10,10,.8), rgba(0,0,0,.3)) !important;
            border-radius: 5px !important;  border: none !important;
            box-shadow: inset rgba(0,0,0,.6) 0 -2px  2px 2px !important;
            transition: box-shadow .25s ease !important;
            padding: 2px !important;    }

textbox[focused="true"]{   box-shadow: inset rgba(0,0,0,.6) 0 2px 2px 2px, 
                                       rgb(/*[[bc]]*/) 0 0 1px, 
                                       rgb(/*[[dc]]*/) 0 0 5px !important;   }

#main-window[privatebrowsingmode]             
textbox[focused="true"]{   box-shadow: inset rgba(0,0,0,.6) 0 2px 2px 2px, 
                                       rgb(/*[[plc]]*/) 0 0 1px, 
                                       rgb(/*[[plc]]*/) 0 0 3px, 
                                       rgb(/*[[pdc]]*/) 0 0 5px !important;   }

/* ::::: Input Boxes for Dark Themes :::::  
@-moz-document url-prefix(http://), url-prefix(ftp://), url-prefix(file://), url-prefix(https://){ 
select, textarea, input
             {     color: #000 !important;
