css:
/* AGENT_SHEET */
/* SCROLLBAR */

scrollbar thumb:not(:hover)
{ opacity: 0.8 !important;
width:5px !important;
}
scrollbar[orient="horizontal"] {height: 5px !important}

scrollbar thumb {-moz-appearance: none !important;
border: none !important;
background: /*[[unhovered]]*/ !important;
}

scrollbar>slider {
background: /*[[background]]*/ !important;
}

scrollbar thumb:active {
background: /*[[clickcolor]]*/ !important;
}


/* hide scrollbar up/down buttons */
scrollbar scrollbarbutton {visibility: collapse !important;}
