css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#statusbar-display:not([label^="Looking"]):not([label^="Connecting"]):not([label^="Connected"]):not([label^="Transferring"]):not([label^="Waiting"]):not([label^="Read"])
 {
   font-size:12px !important;
   max-width: 100% !important;
    /* font-family: Choose One !important; */
}

#statusbar-display{
position:fixed !important;
top:70px !important; /*here line 14*/
left:auto !important;
right:auto !important;
margin:0 auto !important;
width: 100% !important;
text-align:center !important;
opacity:/*[[settingplaceholder]]*/  !important;
}
#statusbar-display[inactive]{
opacity:0 !important;
}
.statuspanel-inner{
width: auto !important;
margin:0 auto !important;
text-align:center !important;
}
.statuspanel-label{
border-top:none !important;
border-left:1px solid #BBB !important;
border-bottom:1px solid #BBB !important;
border-right:1px solid #BBB !important;
border-radius:0 0 3px 3px !important;
}
