@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/* AGENT_SHEET */

#navigator-toolbox::after {
  background-color: #AAAAAB !important;
}

.bookmark-item > .toolbarbutton-text:not(:-moz-lwtheme) {
  color: #000000 !important;
}

#nav-bar,
#nav-bar[collapsed=true]:not([customizing]) + toolbar,
#nav-bar[collapsed=true]:not([customizing]) + #customToolbars + #PersonalToolbar {
  background-image: -moz-linear-gradient(#E3E3E4, #DDDDDE, #D7D7D8)!important;
}

#navigator-toolbox[tabsontop="true"] #nav-bar:not(:-moz-lwtheme) {
  box-shadow: 0px 1px 0 0 #F1F1F2 inset !important;
}

#navigator-toolbox[tabsontop="true"]:-moz-lwtheme > #nav-bar,
#navigator-toolbox[tabsontop="true"]:not([customizing]):-moz-lwtheme > #nav-bar[collapsed="true"] + toolbar,
#navigator-toolbox[tabsontop="true"]:not([customizing]):-moz-lwtheme > #nav-bar[collapsed="true"] + #customToolbars + #PersonalToolbar {
  background-image: -moz-linear-gradient(rgba(227,227,228,1), rgba(227,227,228,0)) !important;
}

#navigator-toolbox[tabsontop="true"]:not(:-moz-lwtheme) > #PersonalToolbar {
  background-image: -moz-linear-gradient(#D7D7D8, #D3D3D4, #D2D2D3) !important;
}

#main-window[tabsontop=true][disablechrome] #navigator-toolbox::after {
  -moz-border-top-colors: transparent rgba(0,0,0,.3) !important;
  background-image: -moz-linear-gradient(#F1F1F2, #E2E2E3, #E2E2E3) !important;
}

#addons-page #header {
  background-image: -moz-linear-gradient(#E3E3E4, #F2F2F2)!important;
}

#addon-bar:not(:-moz-lwtheme) {
  border-top: 1px solid #AAAAAB!important;
  border-bottom-style: none!important;
  background: #CBCBCB !important;
}

#urlbar,
.searchbar-textbox {
/*[[chromeos]]*/
}
