css:
@-moz-document domain("www.metafilter.com") {
* {
font-family: Arial !important;
letter-spacing: .03em !important;
}

.sidebar, .tags, #footer, #related, #content, #topline, #yellowbar, #bottomline, #maintab, div.comments + div.comments, p.copy.whitesmallcopy {
display: none !important;
}

#logo {
top: 1px;
left: 2px; 
height: 14px !important;
position: absolute;
background: url('http://i.imgur.com/BU9IA.png') no-repeat;
}
#logo a img {
visibility: visible !important;
opacity: 0 !important;
height: 15px !important;
width: 80px !important;
}

a {
color: /*[[acolor]]*/ !important;
}

a:hover {
color: /*[[ahover]]*/ !important;
}

div.copy {
margin-right: 25% !important;
}

div.comments {
margin-top: 30px !important;
padding-right: 30% !important;
}

div.copy.post {
padding: 10px 0 7px 0 !important;
border-bottom: 1px solid #ddd !important;
}

#posts {
margin: 0 !important;
}

div#posts div {
line-height: 1.6em !important;
margin: 30px 35% 0 4% !important;
}

body {
background: /*[[bodybg]]*/ !important;
color: #000 !important;
}

h1[class~="posttitle"], #posts h2 {
color: #777 !important;
background: #eee !important;
margin: 0 0 25px 0 !important;
padding: 10px 0 10px 25px !important;
font-family: Georgia !important;
}
h1[class~="posttitle"] span {
display: none !important;
margin: 6px 0 0 2px !important;
color: #888 !important;
}
h1[class~="posttitle"] span a {
display: none !important;
}

div.copy:first {
background: #444 !important;
color: #ddd !important;
}

div.copy span.smallcopy:last-child {
display: block !important;
color: #aaa !important;
margin: 18px 0 -6px 3px !important;
text-align: right !important;
}

span.smallcopy a:nth-of-type(1) {
color: #999 !important;
}

.comments a:nth-of-type(2) {
color: #999 !important;
}

div.comments span.smallcopy {
text-align: right;
display: block !important;
margin-top: 25px !important;
padding-bottom: 5px !important;
border-bottom: 1px solid #ddd !important;
}

div.comments span.smallcopy a:nth-child(3) {
float: left !important;
}

#posts br + h2 {
margin-top: 25px !important;
}
}
