css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("megatokyo.com") { *{/*$*/}
    #adbar, #banner, #metabox, #support, #comic>form, #comic>ul, #console>h2, #credits {
        display:        none !important;
    }
    #comic {
        position:       absolute !important;
            top:        14px !important;
            left:       50% !important;
        margin-left:    -350px !important;
    }
    #console { 
        width:          100% !important;
        position:       absolute;
            top:        0px !important;
            left:       0px !important;
        overflow:       visible !important;
    }
    #console>div.leftcol, #console>div.rightcol {
        visibility:     /*[[RANTS]]*/ !important;
    }
}
