css:
@-moz-document url-prefix("http://www.reddit.com/r/Minecraft") {
.score.dislikes, .score.unvoted, .score.likes 

{

    height: 14px !important;

    margin-top: -2px;

}



span.rank, .thumbnail {padding-top: .45%;}



.arrow.upmod, .arrow.downmod, .arrow.up, .arrow.down 

{

    background-image: url(/*[[imageuse]]*/) !important; 

    width: 15px !important;

    height: 20px !important;

}



.arrow.upmod {background-position: -41px 0px !important;}

.arrow.downmod {background-position: -3px 0px !important;}

.arrow.up {background-position: -60px 0px !important;}

.arrow.down {background-position: -22px 0px !important;}



.midcol[style] 

{

    width: 35px !important;

    margin-right: 2px !important;

}



.midcol { overflow: visible !important; margin-right:8px !important; }



.click-gadget .midcol { margin: 0 !important; }

}
