css:
@-moz-document domain("mail.google.com") {

/* Unread rows - background and text */

.zE {background-color: /*[[unreadcolor]]*/ !important; color: /*[[unreadtext]]*/ !important;}


/* Read rows - background and text */

.yO {background-color: /*[[readcolor]]*/ !important; opacity: .9 !important; color: /*[[readtext]]*/ !important;}


/* Selected rows */

.x7 {background-color: /*[[selectedcolor]]*/ !important;}


/* Row dividers */

.xY {border-color: /*[[dividercolor]]*/ !important;}


/* Alternating colours in Conversation View for clearer separation of messages */

div.h7:nth-of-type(odd) div.adn.ads, div.kv:nth-of-type(odd){ background-color: /*[[oddcolor]]*/; transparent;}

div.h7:nth-of-type(even) div.adn.ads, div.kv:nth-of-type(even){ background-color: /*[[evencolor]]*/; transparent; }


}
