css:
@-moz-document url-prefix(http), url-prefix(ftp:), url-prefix(file:), url-prefix(about:neterror), url-prefix(about:blank) {

html {background: #000}

html {
-webkit-filter: grayscale(1) invert(1) brightness(150%) contrast(80%);
filter: grayscale(1) invert(1) brightness(150%) contrast(80%);
min-height: 100% !important;
}

img {
-webkit-filter: invert(1);
filter: invert(1);
}

}
