css:
/***************************************************************************************/
/* Facebook Smooth (Version 1.1)                                                       */
/* Author: Tristwin                                                                    */
/* Background: http://browse.deviantart.com/?qh=&section=&q=background+mesh#/d30hb7r   */
/* Updated: 20th July, 2012                                                            */
/***************************************************************************************/
                                                                            
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {

/* hidden elements */

.div.storyHighlightIndicatorWrapper{ display:none; }
.uiScrollableAreaGripper{ display:none; }


 #pagelet_welcome_box{
        display:none;
}
   
    
#boulder_fixed_header{ 
     margin-top: 15px;
     margin-bottom: 15px;
     font-size: 0.7em;
}

#blueBar{ 
     height:43px !important;
     border-color: #000 !important;  
     box-shadow: 0 10px 7px rgba(0,0,0,0.9);
}

#pagelet_bluebar{ 
     box-shadow: 0 10px 7px rgba(0,0,0,0.9);
}

#contentCol{
     margin-top:15px !important;
}




/* box shadows on elements */

#pagelet_composer{
     box-shadow: 5px 5px 7px rgba(132,132,132,0.9);
     border: 5px solid #ffffff;
     margin-top: 10px;
     margin-bottom: 10px;
}

.clearfix.storyContent{
     box-shadow: 5px 5px 7px rgba(132,132,132,0.9);
     border: 5px solid #ffffff;
     margin-top: 10px;
     margin-bottom: 10px;
}

#leftCol{ 
     box-shadow: -10px 0px 0px rgba(0,0,0,0.9);
}

#fbPhotoPageContainer{ 
     box-shadow: 10px 0px 0px rgba(132,132,132,0.9);
}

/* change fonts */

*{font-family:"/*[[myfont]]*/" !important;}
*{font-size: inherit !important;}
body {font-size: 0.7em !important;}
.navHeader {font-size: 0.9em !important;}
.uiSideNav { color: #ffffff !important;}

/* timeline specific changes */

#pagelet_main_column_personal{
     background-color: rgba(0,0,0,0.9);
     box-shadow: 0 0 10px 10px rgba(0,0,0,0.9);
}

.fbTimelineUnit{
}

/* remove lightbox */

#fbPhotoPageContainer{ 
     background-color: #fff !important;
     border:10px solid rgba(225,225,225,0.8) !important;
}

/* left column changes */

#leftCol{
     background-color: rgba(0,0,0,0.9) !important; 
     border:none;
}

.uiSideNav a{ 
     border: none !important; 
}

.uiSideNav a:hover{
     background-color: rgba(100,100,100,0.4) !important;
}

.linkWrap.noCount, .linkWrap.hasCount{
     color: rgba(225,225,225,0.8) !important;
}

.uiSideNav .selectedItem  .item, .uiSideNav .selectedItem .item:hover, 
.uiSideNav ul .selectedItem .subitem, .uiSideNav ul .selectedItem .subitem:hover {
    background-color: rgba(59,89,152,0.4) !important;
}


/* main background image */

body{
background: url("http://i60.tinypic.com/mmzc3l.jpg") bottom center fixed !important;
}


/* hide advertisments */

.fbTimelineSideAds,

#rightCol 
 
.ego_column:nth-child(1),
.fbPhotoSnowboxAds,
.fbPhotoSnowboxAdsSide,
.fbEmu.fbEmuBlock.fbEmuEgo[id*="-id_"],


.fbEmuMarketplace,
#egoRefreshAds,
#pagelet_ego_pane_m,
#pagelet_ego_pane_w,
#pagelet_organic_ego_pane,
#pagelet_ego_pane

.profile_sidebar_ads,
.sidebar_ads, 
.adcolumn_header, 
.admarket_ad, .ad_story, 
.social_ad_advert, 
.ads_feedback, 
.next_ad_button, 
.more_ads

{background:none !important; display:none !important} 

}
