@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
/*מעבר על תפריט הגדרות לא כולל התנתקות*/
#navAccount .navigation a:hover {
   background-color: /*[[jkk]]*/!important ;
   color: white!important;
} 
/*if you want left to right delete until*/
/*תיקון טקסט שמאל*/
.left-aligned-text {
    text-align: right!important;
    direction: rtl!important;
}
/*delete untill here*/
/*רקע של הראש כולו*/
#blueBar{
    background-color: /*[[cp]]*/!important ;
     -webkit-transition:background-color 1s!important;
     -moz-transition:background-color 1s!important;
     -o-transition:background-color 1s!important;
     transition:background-color 1s!important;
     /*position:fixed!important;*/
}
#blueBar:hover{
    background-color: /*[[gps]]*/!important ;
}
דף הבית, חברים, הודעות והתראות/*
לוגו וקישורי: חברים, הודעות והתראות
כאשר העכבר עליהם*/
.slim #pageNav .middleItem a:hover, .slim #pageNav .topNavLink:focus, .slim #pageNav .topNavLink:active, .slim #pageNav .tinyman:hover a, .slim #pageNav .tinyman:focus a, .slim #pageNav .tinyman:active a, .slim #pageNav .topNavLink a:hover, .slim #pageNav .topNavLink a:focus, .slim #pageNav .topNavLink a:active, #navAccountLink a:hover, #pageLogo a:hover, .slim #fbRequestsJewel a:hover, .slim #fbMessagesJewel a:hover, .slim #fbNotificationsJewel a:hover {
     background-color: /*[[jkk]]*/!important ;
     -webkit-transition:background-color 1s!important;
     -moz-transition:background-color 1s!important;
     -o-transition:background-color 1s!important;
     transition:background-color 1s!important;
}
.slim #pageNav .middleItem a:hover, .slim #pageNav .topNavLink:focus, .slim #pageNav .topNavLink:active, .slim #pageNav .tinyman:hover a, .slim #pageNav .tinyman:focus a, .slim #pageNav .tinyman:active a, .slim #pageNav .topNavLink a:hover, .slim #pageNav .topNavLink a:focus, .slim #pageNav .topNavLink a:active, #navAccountLink a:hover, #pageLogo a:hover, .slim #fbRequestsJewel a:hover, .slim #fbMessagesJewel a:hover, .slim #fbNotificationsJewel a:hover {
     background-color: /*[[jkk]]*/!important ;
     -webkit-transition:background-color 1s!important;
     -moz-transition:background-color 1s!important;
     -o-transition:background-color 1s!important;
     transition:background-color 1s!important;
}
/*חץ הגדרות*/
#navAccountLink .menuPulldown:hover {
     background-color: /*[[jkk]]*/ ;
     background-repeat: no-repeat!important;
     -webkit-transition:background-color 1s!important;
     -moz-transition:background-color 1s!important;
     -o-transition:background-color 1s!important;
     transition:background-color 1s!important;
}
/*קישורי הגדרות*/
#pageNav #navAccount .navSubmenu:hover,
#pageNav #navAccount .navSubmenu:focus,
#pageNav #navAccount .navSubmenu:active {
     background: #00A3FF!important;
     border-bottom: 1px double #0CF!important;
     border-top: 1px double #0CF!important;
     -webkit-transition:background-color 2s!important;
     -moz-transition:background-color 2s!important;
     -o-transition:background-color 2s!important;
     transition:background-color 2s!important;

}
a .navLink bigPadding:hover  {
     background-color: /*[[jkk]]*/ ;
     -webkit-transition:background-color 1s!important;
     -moz-transition:background-color 1s!important;
     -o-transition:background-color 1s!important;
     transition:background-color 1s!important;  
}
}
