css:
@-moz-document domain("mlpchan.net") {
@font-face {
font-family:'Goudy Bookletter 1911' !important;
src: url('http://zashy.bitbucket.org/resources/font/goudy_bookletter_1911.eot') !important;
src: url('http://zashy.bitbucket.org/resources/font/goudy_bookletter_1911.eot?#iefix') format('eot'), url('http://zashy.bitbucket.org/resources/font/goudy_bookletter_1911.woff') format('woff'), url('http://zashy.bitbucket.org/resources/font/goudy_bookletter_1911.ttf') format('truetype'), url('http://zashy.bitbucket.org/resources/font/goudy_bookletter_1911.svg#FontAwesome') format('svg') !important;
font-weight: normal !important;
font-style: normal !important;
}
body{
font-family:'Goudy Bookletter 1911',arial,serif !important;
font-size:14px !important;
}
a, a:visited, div.post.reply .body a, div.post.reply .body a:visited, #qrDisplayButton{
transition:color 3s, text-shadow 1s !important;
-webkit-transition:color 3s, text-shadow 1s !important;
-moz-transition:color 3s, text-shadow 1s !important;
-o-transition:color 3s, text-shadow 1s !important;
}
img{
filter: gray !important;
-webkit-filter: grayscale(1) !important;
transition:filter 1s !important;
-webkit-transition:-webkit-filter 1s !important;
-moz-transition:filter 1s !important;
-o-transition:filter 1s !important;
}
img:hover, img.image-hover{
filter:none !important;
-webkit-filter: grayscale(0) !important;
}
/*menu bars*/
.boardlist.top{
margin:0 !important;
left:0 !important;
height:5px !important;
overflow:hidden !important;
transition:height 1s ease-in-out, box-shadow 1s !important;
-webkit-transition:height 1s ease-in-out, box-shadow 1s !important;
-moz-transition:height 1s ease-in-out, box-shadow 1s !important;
-o-transition:height 1s ease-in-out, box-shadow 1s !important;
z-index:1 !important;
}
.boardlist.top:before{
position:fixed !important;
top:0 !important;
left:0 !important;
width:100% !important;
height:15px !important;
content:"" !important;
z-index:-1 !important;
}
.boardlist.top:after{
position:absolute !important;
top:0 !important;
left:0 !important;
width:100% !important;
height:35px !important;
content:"" !important;
z-index:-1 !important;
}
.boardlist.top:hover{height:32px !important;}
.boardlist.top .boardlistpart a.boardlistactive, .boardlist.top .boardlistpart a.boardlistactive:visited{
padding:2px 4px !important;
}
.boardlist.top .boardlistpart a, .boardlist.top .boardlistpart a:visited{
padding:2px 0 !important;
margin:0 2px !important;
transition:padding 1s, color 1s, background-color .6s, border-top-color 1s !important;
-webkit-transition:padding 1s, color 1s, background-color .6s, border-top-color 1s !important;
-moz-transition:padding 1s, color 1s, background-color .6s, border-top-color 1s !important;
-o-transition:padding 1s, color 1s, background-color .6s, border-top-color 1s !important;
}
.boardlist.top .boardlistpart a[href="/"], .boardlist.top .boardlistpart.settingsButton a{
padding:2px 4px !important;
text-transform:capitalize !important;
}
.boardlist.top .boardlistpart a:hover{
padding:2px 4px !important;
}
.boardlist.bottom{font-size:12px !important;margin-top:40px !important;}
.boardlist.bottom .boardlistpart:first-child, .boardlist.bottom .boardlistpart:last-child{text-transform:capitalize !important;}
/*title*/
header h1{
font-size:32px !important;
}
header div.subtitle{
font-size:12px !important;
}
header h1, header div.subtitle{font-family:monospace !important;}
/*post form*/
form table{border-spacing:4px !important;}
form table tr th{
padding:5px !important;
border-radius:10px !important;
margin:3px 0 !important;
}
form table tr td{
margin:3px 0 !important;
}
input[type="text"], input[type="password"], textarea{
vertical-align:middle !important;
font-size:14px !important;
font-family:'Goudy Bookletter 1911',arial,serif !important;
transition:border-color 1s, outline-color 1s !important;
-webkit-transition:border-color 1s, outline-color 1s !important;
-moz-transition:border-color 1s, outline-color 1s !important;
-o-transition:border-color 1s, outline-color 1s !important;
width:200px !important;
}
textarea{width:300px !important;}
input[type="text"], input[type="password"]{padding:4px 0 !important;}
/*qr box*/
body>div:last-of-type{
border-radius:10px !important;
}
#qrname, #qremail, #qrsubject{width:33.3% !important;}
#qrform textarea{width:100% !important;}
/*Settings*/
#settingsScreen{
padding: 2px 1px !important;
}
#settingsScreen h1{color:#eee !important;font-family:'Goudy Bookletter 1911',arial,serif !important;}
/*posts*/
div.post.reply{
padding:5px !important;
border-radius:15px !important;
padding-bottom:16px !important;
margin-bottom:6px !important;
max-width:98% !important;
transition:box-shadow 2s !important;
-webkit-transition:box-shadow 2s !important;
-moz-transition:box-shadow 2s !important;
-o-transition:box-shadow 2s !important;
}
p.intro span.subject{font-size:16px !important;}
p.intro a.email{text-decoration:none !important;}
span.trip:first-letter{letter-spacing:1px !important;}
.hashtag{margin-bottom:3px !important;}
div.post.reply.post-hover{
border-top-right-radius:0 !important;
border-bottom-left-radius:0 !important;
z-index:10 !important;
}
/*pages*/
div.pages {
border-right:none !important;
border-bottom:none !important;
}
/*delete area*/
form[name="postcontrols"]{position:relative !important;}
div.delete{
float:none !important;
position:absolute !important;
right:10px !important;
bottom:-60px !important;
display:inline-block !important;
border-top-left-radius:10px !important;
border-top-right-radius:10px !important;
padding:10px 5px !important;
width:340px !important;
text-align:right !important;
}
div.delete input[type="text"], div.delete input[type="password"]{
width:80px !important;
}
div.delete:nth-last-of-type(2){
bottom:-100px !important;
}
div.delete:nth-last-of-type(1){
bottom:-140px !important;
border-bottom-left-radius:10px !important;
border-bottom-right-radius:10px !important;
border-top-left-radius:0 !important;
border-top-right-radius:0 !important;
}
footer{margin-top:40px !important;}
/*[[selectedColor]]*/
}
