		/* Body */
		
			/* Homepage */
			
				div#wrapper
				{
					background-color: #EEE !important;
				}
				div#wrapper div#content
				{
					margin-top: 0px !important;
				}
				div.section.half-left div.output div.person div.head
				{
					position: relative !important;
				}
				div.section.half-left div.output div.person div.head a:not(.photo)
				{
					position: absolute !important;
					left: 2px !important;
				}
				div#right div#events div.filter
				{
					width: 100% !important;
				}
				
		/* Bottom - Footer */
		
			div#footer div.content:after
			{
				content: "The \"MocoSpace - Simplified\" theme was created by XDieAndGoX." !important;
				margin-left: 100px !important;
				color: rgb(90,90,90) !important;
				font-size: 8pt !important;
			}

