css:
@-moz-document url-prefix("https://docs.google.com/document/") {
body { color: #666 !important; }
#docs-chrome { background: black !important; }
.docs-titlebar-buttons { opacity: 0.4; }

#docs-toolbar-wrapper,
.goog-toolbar { background: #454443 !important; }

#docs-toolbar-wrapper, .goog-toolbar-separator {
border-color: #555453 !important;
}
.goog-toolbar-button-selected, .goog-toolbar-button-checked, .goog-toolbar-menu-button-open { background: #555453 !important; border-color: #656463 !important; }
#docs-editor { background: #353433 !important;}

.kix-ruler-background { background: #252423 !important; 
border-color: #252423 !important; }
.kix-ruler-background-inner { background: #454443 !important; }
.kix-ruler-face-minor-division, .kix-ruler-face-major-division {
border-color: #555453 !important;
}

.docs-activity-indicator-container { opacity: 0.3; }
.kix-page {
background-color: #090807 !important;
border-color: #454443 !important;
}
.kix-cursor-caret { border-color: /*[[caret]]*/ !important; 
display: block !important;
box-shadow: 0 0 10px /*[[caret]]*/,
0 0 20px /*[[caret]]*/,
0 0 40px /*[[caret]]*/;
}

.kix-ruler-indent-first-line,
.kix-ruler-indent-left, .kix-ruler-indent-right { opacity: 0.5; }

.kix-page, .kix-page * { color: #e9e8e7 !important; }

.docs-reference-pane-container { border-color: #555453 !important; }


}
