  .userDetail .regist,
 
 /*ヘッダのユーザーアイコン*/
/*[[header_usericon]]*/
 
 /*ヘッダのお知らせアイコン*/
 /*#siteHeaderNotificationContainer,*/
 
 /*ニコニ広告*/
 #uad,
 #nicoads,

 /*DMMちゃんねる*/
 #R18-Banner,

 /*宣伝コメント（ランキング）*/
 .thumb_uad_header,
 .thumb_uad_point,
 .userAd,

 /*宣伝コメント（トップページ・検索ページ）*/
 /*.uad_comment,*/

 /*フッター*/
 #footerWrap,
 #PAGEFOOTER

 { display: none !important; }
 }

/*= 動画ページの項目 =*/
@-moz-document url-prefix(http://www.nicovideo.jp/watch/),
url-prefix(http://www.nicovideo.jp/playlist/mylist/){

 /*動画レビュー*/
/*[[watch_review]]*/

 /*ニコニコ市場*/
/*[[watch_ichiba]]*/

 /*プレイリスト*/
/*[[watch_playlist]]*/
 
 /*ニュース*/
/*[[watch_news]]*/

 /*ニコニコアプリ*/
/*[[watch_app]]*/

/*[[watch_bottom_info]]*/

 /*フィードバックリンク*/
 #feedbackLink,

 /*フッター*/
 #footer

 { display: none !important; }
 }


/*****************
---ヘッダ変更---
******************/
/*[[header]]*/

/*********
---全体---
**********/
@-moz-document url-prefix(http://www.nicovideo.jp/){

/*ページナビゲーションボタン*/
.pager a{
	background: url("http://res.nimg.jp/img/recommendations/more_bg.png") !important;
}
.pager a,
.pager span{
	border-radius: 2px;
	padding: 3px 8px 2px 8px !important;
	border: 1px solid #d5d5d5 !important;
	color: #777 !important;
	font-size: 11px !important;
}
.pager a:hover,
.pager span{
	background-image: none !important;
	background-color: #ddd !important;
	border: 1px solid #ccc !important;
	color: #777 !important;
	background-image: -moz-linear-gradient(top, #dcdcdc, #e6e6e6 8%, #d0d0d0 98%) !important;
}

/*見出し*/
H1{
	font-size: 18px !important;
	line-height: 1.5 !important;
}

/*リンク色変更*/
a.watch:link{
	color: #558293 !important;
}
a.watch:visited{
	color: #2d4d55 !important;
}
a.watch:hover{
	background-color: #b5cdce !important;
}
a.watch:active{
	color: #DE822E !important;
	background-color: #F4DC9B !important;
}

/*AutoPagerize*/
hr.autopagerize_page_separator{
	border: 1px dotted #ccc !important;
	margin: 9px 0 !important;
}
.autopagerize_page_info{
	width: 60px !important;
	text-align: center !important;
	padding: 1px 7px 2px !important;
	margin-bottom: 13px !important;
	background: #59818a !important;
	color: #fff !important;
	font-size: 11px !important;
	border-radius: 8px;
	box-shadow: 0 1px 2px rgba(0, 0, 0, 0.25);
	text-shadow: 0 -1px 1px rgba(0, 0, 0, 0.25);
}
.autopagerize_page_info > .autopagerize_link{
	color: #fff !important;
	font-weight: bold;
	text-decoration: none !important;
}
#autopagerize_icon{
	width: 8px !important;
	height: 8px !important;
	margin-right: 5px !important;
	margin-top: 6px !important;
	border-radius: 5px;
	box-shadow: 0 0 2px #aaa;
	z-index: 999999 !important;
}
#autopagerize_help{
	z-index: 999999 !important;
}
}

