*****************/
@-moz-document url("http://www.nicovideo.jp/"),
url("http://www.nicovideo.jp/uni_ranking"),
url("http://www.nicovideo.jp/stamp/nico_ii_newvideo_mylist"){

/*総合ランキング*/
.cmnHeading{
	height: 20px !important;
}
.mainHeading{
	background: url("http://uni.res.nimg.jp/img/bg/bg_common_c_head.gif") !important;
	color: #222 !important;
	padding-left: 10px !important;
}

/*広告空白*/
#sideContents > div[style$="240px;"]:first-child{
	display: none !important;
}
}

/************************
---広告削除（ニコ動のみ）---
*************************/
@-moz-document domain("nicovideo.jp"){
 .adBnr,
 #pageFooterAds,
 #playerBottomAd,
 #ichibaPanelAd,
 #ads_undefined_Left,
 #ads_undefined_Right,
 #web_pc_360,
 #web_pc_top,
 #web_pc_footer,
 #web_pc_prime,
 #web_pc_premium,
 .ads_468,
 .ads_728_google,
 .sidewallAd,
 .sideTxtAd,
 script + script + script +ins,
 .watch_tag,
 .watch_tag_sync,
 #WATCHFOOTER > .mb16p4
 { display: none !important; }
}
