css:
@-moz-document domain(tumblr.com) {
#discover_button.tab.iconic > a,
#discover_button.tab.iconic.selected > a {
  background-image: url("http://media.tumblr.com/92c514dc3b3cafe8c4610cae332e4b93/tumblr_inline_miceouN1KK1qz4rgp.png") !important;
  background-size: 37px 37px !important;
  background-repeat: no-repeat !important;
  opacity: .65 !important;
}
#discover_button.tab.iconic > .tab_anchor::before {
	content: none !important;
}
#discover_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 36px !important;
}}
