css:
@-moz-document url-prefix(http://www.tumblr.com/),url-prefix(https://www.tumblr.com/){


/* ======= Background ======= */

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,#dashboard_post_queue, #dashboard_followers, #dashboard_members{background: url(/*[[bgimage]]*/) fixed !important;}

#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content,#dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0,0,0,0) !important}






/* ======= Dashboard Style ======= */


#left_column #posts .notification .avatar {opacity:0.7 !important;}
#left_column #posts .notification .avatar:hover{opacity:1 !important;}

#right_column{background-color: #FFFFFF opacity:1 !important;}

#new_post_notice_container{color:#fff!important}


#posts .notification {
	opacity:0.8 !important;
	background:  #163156 !important;
	border: 1px #000000 solid !important;
	margin: -1px;
	}

#posts .notification .nipple.border, #posts .notification.alt .nipple.border {
	border-right: 7px solid #000000 !important;
	}

#posts .notification .nipple, #posts .notification.alt .nipple {
	border-right: 6px solid #163056 !important;
	}
}
