css:
@-moz-document regexp('(http://|https://|)(www.|)tumblr.com(.*)') {

body{
background: #62472C /*[[yehtree]]*/;
background-repeat: no-repeat;
background-position-y: 100%;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: url('http://i.imgur.com/6MNVM.png') top repeat-x;
}

#right_column::after{
background:;
height:0px;
}

.selection_nipple{
border-bottom: 12px solid #A77747 !important;
}

#dashboard_controls_open_blog #open_blog_link {
color: #DBDBDB !important;
}

.permalink{
background: url('http://i.imgur.com/VynYb.png') !important;
}
}


@-moz-document regexp('(http://|)(www.|)tumblr.com/new/(.*)'), regexp('(http://|)(www.|)tumblr.com/reblog/(.*)'){

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
padding-top: 20px !important;
border-radius: 33px;
}

img[src="http://assets.tumblr.com/images/content_top_edit_form.png?alpha"]{
display:none !important;
}

img[src="http://assets.tumblr.com/images/content_bottom_edit_form.png?alpha"]{
display:none !important;
}

#edit_post_controls .advanced_option label {
color: #B4B3B3 !important;
}

body{
color: #B4B3B3 !important;
}

h2{
color: #F0F0F0 !important;
}

#add_photo_link a {
color: #E4E4E4;
}
}

@-moz-document regexp('(http://|)(www.|)tumblr.com/blog/(.*)'), regexp('(http://|)(www.|)tumblr.com/dashboard'){

#posts .notification{
background-color: #7D5A37;
}

#posts .notification .nipple {
border-right: 6px solid #976C41;
}

#posts .notification a:not(.avatar_frame) {
border-bottom: 1px solid #976C41;
}

#posts .notification.alt {
background-color: #8E663D;
}

#posts .notification.alt .nipple:not(.border) {
border-right: 6px solid #6E4F30;
}

#posts .notification.alt a:not(.avatar_frame).alt {
border-bottom: 1px solid #6E4F30;
}
}

@-moz-document regexp('(http://|)(www.|)tumblr.com/help'), regexp('(http://|)(www.|)tumblr.com/following/(.*)'), regexp('(http://|https://|)(www.|)tumblr.com/blog/(.*)/settings(/|)'){

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: white url('http://www.tumblr.com/images/content_bg.png') !important;
}

.selection_nipple.white {
border-bottom: 0px white solid !important;
}
}
