css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") 

{

.controls_section li .likes:before
{
position: absolute !important;
height: 30px !important;
line-height: 28px !important;
width: 100px !important;
font: inherit !important;
}

a.likes  div.hide_overflow
{
visibility: hidden !important;
}

.controls_section li .likes:before
{
content: "/*[[werds]]*/" !important;
}

.popover_inner_list > li:nth-child(2) > a:nth-child(1):before
{
position: absolute !important;
height: 30px !important;
line-height: 28px !important;
width: 100px !important;
font: inherit !important;
background-color: #FFF !important;
}

.popover_inner_list > li:nth-child(2) > a:nth-child(1):hover:before
{
background-color: #E6EFF7 !important;
}

.popover_inner_list > li:nth-child(2) > a:nth-child(1):before
{
content: "/*[[werds]]*/" !important;
}

}
